# Python Graph Implementation (Adjacency List)
from collections import defaultdict, deque

class Graph:
    def __init__(self, directed=False):
        self.graph = defaultdict(list)
        self.directed = directed
        self.vertices = set()

    def add_vertex(self, vertex):
        """Add a vertex to the graph"""
        self.vertices.add(vertex)
        if vertex not in self.graph:
            self.graph[vertex] = []

    def add_edge(self, u, v, weight=1):
        """Add an edge between vertices u and v"""
        self.add_vertex(u)
        self.add_vertex(v)

        self.graph[u].append((v, weight))
        if not self.directed:
            self.graph[v].append((u, weight))

    def get_neighbors(self, vertex):
        """Get neighbors of a vertex"""
        return self.graph.get(vertex, [])

    def has_edge(self, u, v):
        """Check if edge exists between u and v"""
        neighbors = self.get_neighbors(u)
        return any(neighbor[0] == v for neighbor in neighbors)

    def remove_edge(self, u, v):
        """Remove edge between u and v"""
        if u in self.graph:
            self.graph[u] = [(vertex, weight) for vertex, weight in self.graph[u] if vertex != v]

        if not self.directed and v in self.graph:
            self.graph[v] = [(vertex, weight) for vertex, weight in self.graph[v] if vertex != u]

    def get_vertices(self):
        """Get all vertices in the graph"""
        return list(self.vertices)

    def get_edges(self):
        """Get all edges in the graph"""
        edges = []
        for u in self.graph:
            for v, weight in self.graph[u]:
                if self.directed or u <= v:  # Avoid duplicates in undirected graph
                    edges.append((u, v, weight))
        return edges

    def __str__(self):
        result = []
        for vertex in sorted(self.vertices):
            neighbors = [f"{v}({w})" for v, w in self.graph[vertex]]
            result.append(f"{vertex}: {', '.join(neighbors)}")
        return '\n'.join(result)

