// Java Graph Implementation (Adjacency List)
import java.util.*;

public class Graph<T> {
    private Map<T, List<Edge<T>>> adjacencyList;
    private boolean directed;

    public Graph(boolean directed) {
        this.adjacencyList = new HashMap<>();
        this.directed = directed;
    }

    public Graph() {
        this(false); // Default to undirected
    }

    public static class Edge<T> {
        T destination;
        int weight;

        public Edge(T destination, int weight) {
            this.destination = destination;
            this.weight = weight;
        }

        public Edge(T destination) {
            this(destination, 1);
        }
    }

    public void addVertex(T vertex) {
        adjacencyList.putIfAbsent(vertex, new ArrayList<>());
    }

    public void addEdge(T source, T destination, int weight) {
        addVertex(source);
        addVertex(destination);

        adjacencyList.get(source).add(new Edge<>(destination, weight));

        if (!directed) {
            adjacencyList.get(destination).add(new Edge<>(source, weight));
        }
    }

    public void addEdge(T source, T destination) {
        addEdge(source, destination, 1);
    }

    public List<Edge<T>> getNeighbors(T vertex) {
        return adjacencyList.getOrDefault(vertex, new ArrayList<>());
    }

    public boolean hasEdge(T source, T destination) {
        List<Edge<T>> neighbors = getNeighbors(source);
        return neighbors.stream().anyMatch(edge -> edge.destination.equals(destination));
    }

    public void removeEdge(T source, T destination) {
        List<Edge<T>> sourceNeighbors = adjacencyList.get(source);
        if (sourceNeighbors != null) {
            sourceNeighbors.removeIf(edge -> edge.destination.equals(destination));
        }

        if (!directed) {
            List<Edge<T>> destNeighbors = adjacencyList.get(destination);
            if (destNeighbors != null) {
                destNeighbors.removeIf(edge -> edge.destination.equals(source));
            }
        }
    }

    public Set<T> getVertices() {
        return adjacencyList.keySet();
    }

    public int getVertexCount() {
