using System;
using System.Collections.Generic;
using System.Linq;

public class Graph<T>
{
    private Dictionary<T, List<Edge<T>>> adjacencyList;
    private bool directed;

    public Graph(bool directed = false)
    {
        this.adjacencyList = new Dictionary<T, List<Edge<T>>>();
        this.directed = directed;
    }

    public class Edge<TVertex>
    {
        public TVertex Destination { get; set; }
        public int Weight { get; set; }

        public Edge(TVertex destination, int weight = 1)
        {
            Destination = destination;
            Weight = weight;
        }
    }

    public void AddVertex(T vertex)
    {
        if (!adjacencyList.ContainsKey(vertex))
        {
            adjacencyList[vertex] = new List<Edge<T>>();
        }
    }

    public void AddEdge(T source, T destination, int weight = 1)
    {
        AddVertex(source);
        AddVertex(destination);

        adjacencyList[source].Add(new Edge<T>(destination, weight));

        if (!directed)
        {
            adjacencyList[destination].Add(new Edge<T>(source, weight));
        }
    }

    public List<Edge<T>> GetNeighbors(T vertex)
    {
        return adjacencyList.ContainsKey(vertex) ? adjacencyList[vertex] : new List<Edge<T>>();
    }

    public bool HasEdge(T source, T destination)
    {
        var neighbors = GetNeighbors(source);
        return neighbors.Any(edge => edge.Destination.Equals(destination));
    }

    public void RemoveEdge(T source, T destination)
    {
        if (adjacencyList.ContainsKey(source))
        {
            adjacencyList[source].RemoveAll(edge => edge.Destination.Equals(destination));
        }

        if (!directed && adjacencyList.ContainsKey(destination))
        {
            adjacencyList[destination].RemoveAll(edge => edge.Destination.Equals(source));
        }
    }

    public IEnumerable<T> GetVertices()
    {
        return adjacencyList.Keys;
    }

    public int VertexCount => adjacencyList.Count;

    public int EdgeCount
    {
        get
        {
            int count = adjacencyList.Values.Sum(edges => edges.Count);
            return directed ? count : count / 2;
        }
    }
}

