# Python Heap Implementation (Min Heap)
class MinHeap:
    def __init__(self):
        self.heap = []

    def parent(self, i):
        """Get parent index"""
        return (i - 1) // 2

    def left_child(self, i):
        """Get left child index"""
        return 2 * i + 1

    def right_child(self, i):
        """Get right child index"""
        return 2 * i + 2

    def swap(self, i, j):
        """Swap elements at indices i and j"""
        self.heap[i], self.heap[j] = self.heap[j], self.heap[i]

    def insert(self, value):
        """Insert a value into the heap"""
        self.heap.append(value)
        self._heapify_up(len(self.heap) - 1)

    def _heapify_up(self, i):
        """Maintain heap property upwards"""
        while i > 0 and self.heap[self.parent(i)] > self.heap[i]:
            self.swap(i, self.parent(i))
            i = self.parent(i)

    def extract_min(self):
        """Remove and return the minimum element"""
        if not self.heap:
            raise IndexError("Heap is empty")

        if len(self.heap) == 1:
            return self.heap.pop()

        min_val = self.heap[0]
        self.heap[0] = self.heap.pop()
        self._heapify_down(0)
        return min_val

    def _heapify_down(self, i):
        """Maintain heap property downwards"""
        while self.left_child(i) < len(self.heap):
            min_child_idx = self._get_min_child_idx(i)

            if self.heap[i] > self.heap[min_child_idx]:
                self.swap(i, min_child_idx)
                i = min_child_idx
            else:
                break

    def _get_min_child_idx(self, i):
        """Get index of minimum child"""
        left = self.left_child(i)
        right = self.right_child(i)

        if right >= len(self.heap):
            return left

        return left if self.heap[left] < self.heap[right] else right

    def peek(self):
        """Get minimum element without removing it"""
        if not self.heap:
            raise IndexError("Heap is empty")
        return self.heap[0]

    def size(self):
        """Get heap size"""
        return len(self.heap)

    def is_empty(self):
        """Check if heap is empty"""
        return len(self.heap) == 0

