class TrieNode:
    def __init__(self):
        self.children = {}
        self.is_end_of_word = False
        self.word_count = 0  # Number of words ending at this node

class Trie:
    def __init__(self):
        self.root = TrieNode()

    def insert(self, word):
        """Insert a word into the trie"""
        node = self.root

        for char in word:
            if char not in node.children:
                node.children[char] = TrieNode()
            node = node.children[char]

        node.is_end_of_word = True
        node.word_count += 1

    def search(self, word):
        """Search for a word in the trie"""
        node = self._find_node(word)
        return node is not None and node.is_end_of_word

    def starts_with(self, prefix):
        """Check if any word starts with the given prefix"""
        return self._find_node(prefix) is not None

    def _find_node(self, prefix):
        """Helper method to find node for given prefix"""
        node = self.root

        for char in prefix:
            if char not in node.children:
                return None
            node = node.children[char]

        return node

    def delete(self, word):
        """Delete a word from the trie"""
        def _delete_recursive(node, word, index):
            if index == len(word):
                # We've reached the end of the word
                if not node.is_end_of_word:
                    return False  # Word doesn't exist

                node.is_end_of_word = False
                node.word_count = 0

                # Return True if node has no children (can be deleted)
                return len(node.children) == 0

            char = word[index]
            child_node = node.children.get(char)

            if not child_node:
                return False  # Word doesn't exist

            should_delete_child = _delete_recursive(child_node, word, index + 1)

            if should_delete_child:
                del node.children[char]
                # Return True if node is not end of word and has no children
                return not node.is_end_of_word and len(node.children) == 0

            return False

        _delete_recursive(self.root, word, 0)

    def get_all_words_with_prefix(self, prefix):
        """Get all words that start with the given prefix"""
        words = []
        prefix_node = self._find_node(prefix)

        if prefix_node:
            self._collect_words(prefix_node, prefix, words)

        return words

    def _collect_words(self, node, current_word, words):
        """Helper method to collect all words from a node"""
        if node.is_end_of_word:
            words.append(current_word)

        for char, child_node in node.children.items():
            self._collect_words(child_node, current_word + char, words)

    def count_words(self):
        """Count total number of words in the trie"""
        return self._count_words_recursive(self.root)

    def _count_words_recursive(self, node):
        """Helper method to count words recursively"""
        count = node.word_count
        for child in node.children.values():
            count += self._count_words_recursive(child)
        return count

