// Java Trie Implementation
import java.util.*;

class TrieNode {
    Map<Character, TrieNode> children;
    boolean isEndOfWord;
    int wordCount;

    public TrieNode() {
        children = new HashMap<>();
        isEndOfWord = false;
        wordCount = 0;
    }
}

public class Trie {
    private TrieNode root;

    public Trie() {
        root = new TrieNode();
    }

    public void insert(String word) {
        TrieNode node = root;

        for (char ch : word.toCharArray()) {
            node.children.putIfAbsent(ch, new TrieNode());
            node = node.children.get(ch);
        }

        node.isEndOfWord = true;
        node.wordCount++;
    }

    public boolean search(String word) {
        TrieNode node = findNode(word);
        return node != null && node.isEndOfWord;
    }

    public boolean startsWith(String prefix) {
        return findNode(prefix) != null;
    }

    private TrieNode findNode(String prefix) {
        TrieNode node = root;

        for (char ch : prefix.toCharArray()) {
            if (!node.children.containsKey(ch)) {
                return null;
            }
            node = node.children.get(ch);
        }

        return node;
    }

    public void delete(String word) {
        deleteRecursive(root, word, 0);
    }

    private boolean deleteRecursive(TrieNode node, String word, int index) {
        if (index == word.length()) {
            if (!node.isEndOfWord) {
                return false;
            }

            node.isEndOfWord = false;
            node.wordCount = 0;

            return node.children.isEmpty();
        }

        char ch = word.charAt(index);
        TrieNode childNode = node.children.get(ch);

        if (childNode == null) {
            return false;
        }

        boolean shouldDeleteChild = deleteRecursive(childNode, word, index + 1);

        if (shouldDeleteChild) {
            node.children.remove(ch);
            return !node.isEndOfWord && node.children.isEmpty();
        }

        return false;
    }

    public List<String> getAllWordsWithPrefix(String prefix) {
        List<String> words = new ArrayList<>();
        TrieNode prefixNode = findNode(prefix);

        if (prefixNode != null) {
            collectWords(prefixNode, prefix, words);
        }

        return words;
    }

    private void collectWords(TrieNode node, String currentWord, List<String> words) {
        if (node.isEndOfWord) {
            words.add(currentWord);
        }

        for (Map.Entry<Character, TrieNode> entry : node.children.entrySet()) {
            collectWords(entry.getValue(), currentWord + entry.getKey(), words);
        }
    }

    public int countWords() {
        return countWordsRecursive(root);
    }

    private int countWordsRecursive(TrieNode node) {
        int count = node.wordCount;
        for (TrieNode child : node.children.values()) {
            count += countWordsRecursive(child);
        }
        return count;
    }
}

