    {
        TrieNode node = root;

        foreach (char ch in word)
        {
            if (!node.Children.ContainsKey(ch))
            {
                node.Children[ch] = new TrieNode();
            }
            node = node.Children[ch];
        }

        node.IsEndOfWord = true;
        node.WordCount++;
    }

    public bool Search(string word)
    {
        TrieNode node = FindNode(word);
        return node != null && node.IsEndOfWord;
    }

    public bool StartsWith(string prefix)
    {
        return FindNode(prefix) != null;
    }

    private TrieNode FindNode(string prefix)
    {
        TrieNode node = root;

        foreach (char ch in prefix)
        {
            if (!node.Children.ContainsKey(ch))
            {
                return null;
            }
            node = node.Children[ch];
        }

        return node;
    }

    public void Delete(string word)
    {
        DeleteRecursive(root, word, 0);
    }

    private bool DeleteRecursive(TrieNode node, string word, int index)
    {
        if (index == word.Length)
        {
            if (!node.IsEndOfWord)
            {
                return false;
            }

            node.IsEndOfWord = false;
            node.WordCount = 0;

            return node.Children.Count == 0;
        }

        char ch = word[index];
        if (!node.Children.ContainsKey(ch))
        {
            return false;
        }

        TrieNode childNode = node.Children[ch];
        bool shouldDeleteChild = DeleteRecursive(childNode, word, index + 1);

        if (shouldDeleteChild)
        {
            node.Children.Remove(ch);
            return !node.IsEndOfWord && node.Children.Count == 0;
        }

        return false;
    }

    public List<string> GetAllWordsWithPrefix(string prefix)
    {
        List<string> words = new List<string>();
        TrieNode prefixNode = FindNode(prefix);

        if (prefixNode != null)
        {
            CollectWords(prefixNode, prefix, words);
        }

        return words;
    }

    private void CollectWords(TrieNode node, string currentWord, List<string> words)
    {
        if (node.IsEndOfWord)
        {
            words.Add(currentWord);
        }

        foreach (var kvp in node.Children)
        {
            CollectWords(kvp.Value, currentWord + kvp.Key, words);
        }
    }

    public int CountWords()
    {
        return CountWordsRecursive(root);
    }

    private int CountWordsRecursive(TrieNode node)
    {
        int count = node.WordCount;
        foreach (TrieNode child in node.Children.Values)
        {
            count += CountWordsRecursive(child);
        }
        return count;
    }
