        return f"Deque({self.items})"

# More efficient implementation using circular buffer
class CircularDeque:
    def __init__(self, capacity=10):
        self.capacity = capacity
        self.buffer = [None] * capacity
        self.front = 0
        self.rear = 0
        self.size = 0

    def add_front(self, item):
        if self.size == self.capacity:
            raise OverflowError("Deque is full")

        self.front = (self.front - 1) % self.capacity
        self.buffer[self.front] = item
        self.size += 1

    def add_rear(self, item):
        if self.size == self.capacity:
            raise OverflowError("Deque is full")

        self.buffer[self.rear] = item
        self.rear = (self.rear + 1) % self.capacity
        self.size += 1

    def remove_front(self):
        if self.size == 0:
            raise IndexError("Deque is empty")

        item = self.buffer[self.front]
        self.buffer[self.front] = None
        self.front = (self.front + 1) % self.capacity
        self.size -= 1
        return item

    def remove_rear(self):
        if self.size == 0:
            raise IndexError("Deque is empty")

        self.rear = (self.rear - 1) % self.capacity
        item = self.buffer[self.rear]
        self.buffer[self.rear] = None
        self.size -= 1
        return item

