// More efficient circular buffer implementation
public class CircularDeque<T>
{
    private T[] buffer;
    private int front;
    private int rear;
    private int size;
    private int capacity;

    public CircularDeque(int capacity = 10)
    {
        this.capacity = capacity;
        buffer = new T[capacity];
        front = 0;
        rear = 0;
        size = 0;
    }

    public void AddFront(T item)
    {
        if (size == capacity)
        {
            throw new InvalidOperationException("Deque is full");
        }

        front = (front - 1 + capacity) % capacity;
        buffer[front] = item;
        size++;
    }

    public void AddRear(T item)
    {
        if (size == capacity)
        {
            throw new InvalidOperationException("Deque is full");
        }

        buffer[rear] = item;
        rear = (rear + 1) % capacity;
        size++;
    }

    public T RemoveFront()
    {
        if (size == 0)
        {
            throw new InvalidOperationException("Deque is empty");
        }

        T item = buffer[front];
        buffer[front] = default(T);
        front = (front + 1) % capacity;
        size--;
        return item;
    }

    public T RemoveRear()
    {
        if (size == 0)
        {
            throw new InvalidOperationException("Deque is empty");
        }

        rear = (rear - 1 + capacity) % capacity;
        T item = buffer[rear];
        buffer[rear] = default(T);
        size--;
        return item;
    }

    public int Size => size;

    public bool IsEmpty => size == 0;
}

