import java.util.Random;

public class QuickSort {

    public static void quickSort(int[] arr) {
        if (arr.length <= 1) {
            return;
        }
        quickSortHelper(arr, 0, arr.length - 1);
    }

    private static void quickSortHelper(int[] arr, int low, int high) {
        if (low < high) {
            // Partition the array and get pivot index
            int pivotIndex = partition(arr, low, high);

            // Recursively sort elements before and after partition
            quickSortHelper(arr, low, pivotIndex - 1);
            quickSortHelper(arr, pivotIndex + 1, high);
        }
    }

    private static int partition(int[] arr, int low, int high) {
        // Choose the rightmost element as pivot
        int pivot = arr[high];

        // Index of smaller element
        int i = low - 1;

        for (int j = low; j < high; j++) {
            // If current element is smaller than or equal to pivot
            if (arr[j] <= pivot) {
                i++;
                swap(arr, i, j);
            }
        }

        // Place pivot in correct position
        swap(arr, i + 1, high);
        return i + 1;
    }

    private static int partitionHoare(int[] arr, int low, int high) {
        int pivot = arr[low];
        int i = low - 1;
        int j = high + 1;

        while (true) {
            // Find element on left that should be on right
            do {
                i++;
            } while (arr[i] < pivot);

            // Find element on right that should be on left
            do {
                j--;
            } while (arr[j] > pivot);

            // If elements crossed, partitioning is done
            if (i >= j) {
                return j;
            }

            // Swap elements
            swap(arr, i, j);
        }
    }

    public static void quickSortRandom(int[] arr) {
        quickSortRandomHelper(arr, 0, arr.length - 1);
    }

    private static void quickSortRandomHelper(int[] arr, int low, int high) {
        if (low < high) {
            // Randomly choose pivot and swap with last element
            Random rand = new Random();
            int randomIndex = rand.nextInt(high - low + 1) + low;
            swap(arr, randomIndex, high);

            int pivotIndex = partition(arr, low, high);

            quickSortRandomHelper(arr, low, pivotIndex - 1);
            quickSortRandomHelper(arr, pivotIndex + 1, high);
        }
    }

    private static void swap(int[] arr, int i, int j) {
        int temp = arr[i];
        arr[i] = arr[j];
        arr[j] = temp;
    }

    // Three-way partitioning for arrays with many duplicates
    public static void quickSort3Way(int[] arr, int low, int high) {
        if (low >= high) return;

        int lt = low, gt = high;
        int pivot = arr[low];
        int i = low + 1;

        while (i <= gt) {
            if (arr[i] < pivot) {
                swap(arr, lt++, i++);
            } else if (arr[i] > pivot) {
                swap(arr, i, gt--);
            } else {
                i++;
            }
        }

        quickSort3Way(arr, low, lt - 1);
        quickSort3Way(arr, gt + 1, high);
    }

    // Example usage
    public static void main(String[] args) {
        int[] arr = {64, 34, 25, 12, 22, 11, 90};
        System.out.println("Original array: " + java.util.Arrays.toString(arr));

        quickSort(arr);
        System.out.println("Sorted array: " + java.util.Arrays.toString(arr));
    }
}

