# Python BFS Implementation
from collections import deque, defaultdict

class Graph:
    def __init__(self, directed=False):
        self.graph = defaultdict(list)
        self.directed = directed

    def add_edge(self, u, v):
        self.graph[u].append(v)
        if not self.directed:
            self.graph[v].append(u)

    def bfs(self, start):
        """BFS traversal from start vertex"""
        visited = set()
        queue = deque([start])
        result = []

        visited.add(start)

        while queue:
            vertex = queue.popleft()
            result.append(vertex)

            # Add all unvisited neighbors to queue
            for neighbor in self.graph[vertex]:
                if neighbor not in visited:
                    visited.add(neighbor)
                    queue.append(neighbor)

        return result

    def bfs_shortest_path(self, start, target):
        """Find shortest path between start and target"""
        if start == target:
            return [start]

        visited = set([start])
        queue = deque([(start, [start])])

        while queue:
            vertex, path = queue.popleft()

            for neighbor in self.graph[vertex]:
                if neighbor == target:
                    return path + [neighbor]

                if neighbor not in visited:
                    visited.add(neighbor)
                    queue.append((neighbor, path + [neighbor]))

        return None  # No path found

    def bfs_level_order(self, start):
        """BFS with level information"""
        visited = set([start])
        queue = deque([(start, 0)])
        levels = {}

        while queue:
            vertex, level = queue.popleft()

            if level not in levels:
                levels[level] = []
            levels[level].append(vertex)

            for neighbor in self.graph[vertex]:
                if neighbor not in visited:
                    visited.add(neighbor)
                    queue.append((neighbor, level + 1))

        return levels

    def is_bipartite(self):
        """Check if graph is bipartite using BFS"""
        color = {}

        for start in self.graph:
            if start not in color:
                queue = deque([start])
                color[start] = 0

                while queue:
                    vertex = queue.popleft()

                    for neighbor in self.graph[vertex]:
                        if neighbor not in color:
                            color[neighbor] = 1 - color[vertex]
                            queue.append(neighbor)
                        elif color[neighbor] == color[vertex]:
                            return False

        return True

# Example usage
g = Graph()
g.add_edge(0, 1)
g.add_edge(0, 2)
g.add_edge(1, 3)
g.add_edge(2, 4)
g.add_edge(3, 4)

print("BFS Traversal:", g.bfs(0))
print("Shortest path from 0 to 4:", g.bfs_shortest_path(0, 4))
print("Level order from 0:", g.bfs_level_order(0))
print("Is bipartite:", g.is_bipartite())

