// Java BFS Implementation
import java.util.*;

public class Graph {
    private Map<Integer, List<Integer>> adjacencyList;
    private boolean directed;

    public Graph(boolean directed) {
        this.adjacencyList = new HashMap<>();
        this.directed = directed;
    }

    public Graph() {
        this(false);
    }

    public void addEdge(int u, int v) {
        adjacencyList.computeIfAbsent(u, k -> new ArrayList<>()).add(v);
        if (!directed) {
            adjacencyList.computeIfAbsent(v, k -> new ArrayList<>()).add(u);
        }
    }

    public List<Integer> bfs(int start) {
        Set<Integer> visited = new HashSet<>();
        Queue<Integer> queue = new LinkedList<>();
        List<Integer> result = new ArrayList<>();

        visited.add(start);
        queue.offer(start);

        while (!queue.isEmpty()) {
            int vertex = queue.poll();
            result.add(vertex);

            List<Integer> neighbors = adjacencyList.getOrDefault(vertex, new ArrayList<>());
            for (int neighbor : neighbors) {
                if (!visited.contains(neighbor)) {
                    visited.add(neighbor);
                    queue.offer(neighbor);
                }
            }
        }

        return result;
    }

    public List<Integer> bfsShortestPath(int start, int target) {
        if (start == target) {
            return Arrays.asList(start);
        }

        Set<Integer> visited = new HashSet<>();
        Queue<List<Integer>> queue = new LinkedList<>();

        visited.add(start);
        queue.offer(Arrays.asList(start));

        while (!queue.isEmpty()) {
            List<Integer> path = queue.poll();
            int vertex = path.get(path.size() - 1);

            List<Integer> neighbors = adjacencyList.getOrDefault(vertex, new ArrayList<>());
            for (int neighbor : neighbors) {
                if (neighbor == target) {
                    List<Integer> newPath = new ArrayList<>(path);
                    newPath.add(neighbor);
                    return newPath;
                }

                if (!visited.contains(neighbor)) {
                    visited.add(neighbor);
                    List<Integer> newPath = new ArrayList<>(path);
                    newPath.add(neighbor);
                    queue.offer(newPath);
                }
            }
        }

        return null; // No path found
    }

    public Map<Integer, List<Integer>> bfsLevelOrder(int start) {
        Set<Integer> visited = new HashSet<>();
        Queue<int[]> queue = new LinkedList<>();
        Map<Integer, List<Integer>> levels = new HashMap<>();

        visited.add(start);
        queue.offer(new int[]{start, 0});

        while (!queue.isEmpty()) {
            int[] current = queue.poll();
            int vertex = current[0];
            int level = current[1];

            levels.computeIfAbsent(level, k -> new ArrayList<>()).add(vertex);

            List<Integer> neighbors = adjacencyList.getOrDefault(vertex, new ArrayList<>());
            for (int neighbor : neighbors) {
                if (!visited.contains(neighbor)) {
                    visited.add(neighbor);
                    queue.offer(new int[]{neighbor, level + 1});
                }
            }
        }

        return levels;
    }

    public boolean isBipartite() {
        Map<Integer, Integer> color = new HashMap<>();

        for (int start : adjacencyList.keySet()) {
            if (!color.containsKey(start)) {
                Queue<Integer> queue = new LinkedList<>();
                queue.offer(start);
                color.put(start, 0);

                while (!queue.isEmpty()) {
                    int vertex = queue.poll();

                    List<Integer> neighbors = adjacencyList.getOrDefault(vertex, new ArrayList<>());
                    for (int neighbor : neighbors) {
                        if (!color.containsKey(neighbor)) {
                            color.put(neighbor, 1 - color.get(vertex));
                            queue.offer(neighbor);
                        } else if (color.get(neighbor).equals(color.get(vertex))) {
                            return false;
                        }
                    }
                }
            }
        }

        return true;
    }

    // Example usage
    public static void main(String[] args) {
        Graph g = new Graph();
        g.addEdge(0, 1);
        g.addEdge(0, 2);
        g.addEdge(1, 3);
        g.addEdge(2, 4);
        g.addEdge(3, 4);

        System.out.println("BFS Traversal: " + g.bfs(0));
        System.out.println("Shortest path from 0 to 4: " + g.bfsShortestPath(0, 4));
        System.out.println("Level order from 0: " + g.bfsLevelOrder(0));
        System.out.println("Is bipartite: " + g.isBipartite());
    }
}

