// C# BFS Implementation
using System;
using System.Collections.Generic;
using System.Linq;

public class Graph
{
    private Dictionary<int, List<int>> adjacencyList;
    private bool directed;

    public Graph(bool directed = false)
    {
        this.adjacencyList = new Dictionary<int, List<int>>();
        this.directed = directed;
    }

    public void AddEdge(int u, int v)
    {
        if (!adjacencyList.ContainsKey(u))
            adjacencyList[u] = new List<int>();
        adjacencyList[u].Add(v);

        if (!directed)
        {
            if (!adjacencyList.ContainsKey(v))
                adjacencyList[v] = new List<int>();
            adjacencyList[v].Add(u);
        }
    }

    public List<int> BFS(int start)
    {
        HashSet<int> visited = new HashSet<int>();
        Queue<int> queue = new Queue<int>();
        List<int> result = new List<int>();

        visited.Add(start);
        queue.Enqueue(start);

        while (queue.Count > 0)
        {
            int vertex = queue.Dequeue();
            result.Add(vertex);

            if (adjacencyList.ContainsKey(vertex))
            {
                foreach (int neighbor in adjacencyList[vertex])
                {
                    if (!visited.Contains(neighbor))
                    {
                        visited.Add(neighbor);
                        queue.Enqueue(neighbor);
                    }
                }
            }
        }

        return result;
    }

    public List<int> BfsShortestPath(int start, int target)
    {
        if (start == target)
        {
            return new List<int> { start };
        }

        HashSet<int> visited = new HashSet<int>();
        Queue<List<int>> queue = new Queue<List<int>>();

        visited.Add(start);
        queue.Enqueue(new List<int> { start });

        while (queue.Count > 0)
        {
            List<int> path = queue.Dequeue();
            int vertex = path.Last();

            if (adjacencyList.ContainsKey(vertex))
            {
                foreach (int neighbor in adjacencyList[vertex])
                {
                    if (neighbor == target)
                    {
                        List<int> newPath = new List<int>(path);
                        newPath.Add(neighbor);
                        return newPath;
                    }

                    if (!visited.Contains(neighbor))
                    {
                        visited.Add(neighbor);
                        List<int> newPath = new List<int>(path);
                        newPath.Add(neighbor);
                        queue.Enqueue(newPath);
                    }
                }
            }
        }

        return null; // No path found
    }

    public Dictionary<int, List<int>> BfsLevelOrder(int start)
    {
        HashSet<int> visited = new HashSet<int>();
        Queue<(int vertex, int level)> queue = new Queue<(int, int)>();
        Dictionary<int, List<int>> levels = new Dictionary<int, List<int>>();

        visited.Add(start);
        queue.Enqueue((start, 0));

        while (queue.Count > 0)
        {
            var (vertex, level) = queue.Dequeue();

            if (!levels.ContainsKey(level))
                levels[level] = new List<int>();
            levels[level].Add(vertex);

            if (adjacencyList.ContainsKey(vertex))
            {
                foreach (int neighbor in adjacencyList[vertex])
                {
                    if (!visited.Contains(neighbor))
                    {
                        visited.Add(neighbor);
                        queue.Enqueue((neighbor, level + 1));
                    }
                }
            }
        }

        return levels;
    }

    public bool IsBipartite()
    {
        Dictionary<int, int> color = new Dictionary<int, int>();

        foreach (int start in adjacencyList.Keys)
        {
            if (!color.ContainsKey(start))
            {
                Queue<int> queue = new Queue<int>();
                queue.Enqueue(start);
                color[start] = 0;

                while (queue.Count > 0)
                {
                    int vertex = queue.Dequeue();

                    if (adjacencyList.ContainsKey(vertex))
                    {
                        foreach (int neighbor in adjacencyList[vertex])
                        {
                            if (!color.ContainsKey(neighbor))
                            {
                                color[neighbor] = 1 - color[vertex];
                                queue.Enqueue(neighbor);
                            }
                            else if (color[neighbor] == color[vertex])
                            {
                                return false;
                            }
                        }
                    }
                }
            }
        }

        return true;
    }

    // Example usage
    static void Main(string[] args)
    {
        Graph g = new Graph();
        g.AddEdge(0, 1);
        g.AddEdge(0, 2);
        g.AddEdge(1, 3);
        g.AddEdge(2, 4);
        g.AddEdge(3, 4);

        Console.WriteLine($"BFS Traversal: [{string.Join(", ", g.BFS(0))}]");
        Console.WriteLine($"Shortest path from 0 to 4: [{string.Join(", ", g.BfsShortestPath(0, 4))}]");
        Console.WriteLine($"Level order from 0: {string.Join(", ", g.BfsLevelOrder(0).Select(kvp => $"Level {kvp.Key}: [{string.Join(", ", kvp.Value)}]"))}");
        Console.WriteLine($"Is bipartite: {g.IsBipartite()}");
    }
}

