        }
    }

    static class Node implements Comparable<Node> {
        String vertex;
        int distance;

        Node(String vertex, int distance) {
            this.vertex = vertex;
            this.distance = distance;
        }

        @Override
        public int compareTo(Node other) {
            return Integer.compare(this.distance, other.distance);
        }
    }

    public void addEdge(String u, String v, int weight) {
        adjacencyList.computeIfAbsent(u, k -> new ArrayList<>()).add(new Edge(v, weight));
        if (!directed) {
            adjacencyList.computeIfAbsent(v, k -> new ArrayList<>()).add(new Edge(u, weight));
        }
    }

    public Map<String, Integer> dijkstra(String start) {
        Map<String, Integer> distances = new HashMap<>();
        Map<String, String> previous = new HashMap<>();
        PriorityQueue<Node> pq = new PriorityQueue<>();
        Set<String> visited = new HashSet<>();

        // Initialize distances
        for (String vertex : adjacencyList.keySet()) {
            distances.put(vertex, Integer.MAX_VALUE);
        }
        distances.put(start, 0);

        pq.offer(new Node(start, 0));

        while (!pq.isEmpty()) {
            Node current = pq.poll();
            String currentVertex = current.vertex;

            if (visited.contains(currentVertex)) {
                continue;
            }

            visited.add(currentVertex);

            List<Edge> neighbors = adjacencyList.getOrDefault(currentVertex, new ArrayList<>());
            for (Edge edge : neighbors) {
                if (visited.contains(edge.destination)) {
                    continue;
                }

                int newDist = distances.get(currentVertex) + edge.weight;

                if (newDist < distances.get(edge.destination)) {
                    distances.put(edge.destination, newDist);
                    previous.put(edge.destination, currentVertex);
                    pq.offer(new Node(edge.destination, newDist));
                }
            }
        }

        return distances;
    }

    public List<String> getShortestPath(String start, String end) {
        Map<String, Integer> distances = new HashMap<>();
        Map<String, String> previous = new HashMap<>();
        PriorityQueue<Node> pq = new PriorityQueue<>();
        Set<String> visited = new HashSet<>();

        // Initialize
        for (String vertex : adjacencyList.keySet()) {
            distances.put(vertex, Integer.MAX_VALUE);
        }
        distances.put(start, 0);
        pq.offer(new Node(start, 0));

        while (!pq.isEmpty()) {
            Node current = pq.poll();
            String currentVertex = current.vertex;

            if (visited.contains(currentVertex)) {
                continue;
            }

            visited.add(currentVertex);

            if (currentVertex.equals(end)) {
                break;
            }

            List<Edge> neighbors = adjacencyList.getOrDefault(currentVertex, new ArrayList<>());
            for (Edge edge : neighbors) {
                if (visited.contains(edge.destination)) {
                    continue;
                }

                int newDist = distances.get(currentVertex) + edge.weight;

                if (newDist < distances.get(edge.destination)) {
                    distances.put(edge.destination, newDist);
                    previous.put(edge.destination, currentVertex);
                    pq.offer(new Node(edge.destination, newDist));
                }
            }
        }

        // Reconstruct path
        List<String> path = new ArrayList<>();
        String current = end;

        while (current != null) {
            path.add(current);
            current = previous.get(current);
        }

        Collections.reverse(path);

        if (path.get(0).equals(start)) {
            return path;
        } else {
            return new ArrayList<>(); // No path found
        }
    }

    public int getShortestDistance(String start, String end) {
        Map<String, Integer> distances = dijkstra(start);
        return distances.getOrDefault(end, Integer.MAX_VALUE);
    }

    // Example usage
    public static void main(String[] args) {
        WeightedGraph g = new WeightedGraph(false);

        // Add edges
        g.addEdge("A", "B", 4);
        g.addEdge("A", "C", 2);
        g.addEdge("B", "C", 1);
        g.addEdge("B", "D", 5);
        g.addEdge("C", "D", 8);
        g.addEdge("C", "E", 10);
        g.addEdge("D", "E", 2);

        // Find shortest distances from A
        Map<String, Integer> distances = g.dijkstra("A");
        System.out.println("Shortest distances from A: " + distances);

        // Find shortest path from A to E
        List<String> path = g.getShortestPath("A", "E");
        int distance = g.getShortestDistance("A", "E");
        System.out.println("Shortest path from A to E: " + path + " (distance: " + distance + ")");
    }
}

