// C# Dijkstra's Algorithm Implementation
using System;
using System.Collections.Generic;
using System.Linq;

public class WeightedGraph
{
    private Dictionary<string, List<Edge>> adjacencyList;
    private bool directed;

    public WeightedGraph(bool directed = true)
    {
        this.adjacencyList = new Dictionary<string, List<Edge>>();
        this.directed = directed;
    }

    public class Edge
    {
        public string Destination { get; set; }
        public int Weight { get; set; }

        public Edge(string destination, int weight)
        {
            Destination = destination;
            Weight = weight;
        }
    }

    public class Node : IComparable<Node>
    {
        public string Vertex { get; set; }
        public int Distance { get; set; }

        public Node(string vertex, int distance)
        {
            Vertex = vertex;
            Distance = distance;
        }

        public int CompareTo(Node other)
        {
            return Distance.CompareTo(other.Distance);
        }
    }

    public void AddEdge(string u, string v, int weight)
    {
        if (!adjacencyList.ContainsKey(u))
            adjacencyList[u] = new List<Edge>();
        adjacencyList[u].Add(new Edge(v, weight));

        if (!directed)
        {
            if (!adjacencyList.ContainsKey(v))
                adjacencyList[v] = new List<Edge>();
            adjacencyList[v].Add(new Edge(u, weight));
        }
    }

    public Dictionary<string, int> Dijkstra(string start)
    {
        var distances = new Dictionary<string, int>();
        var previous = new Dictionary<string, string>();
        var pq = new SortedSet<Node>(Comparer<Node>.Create((x, y) => 
        {
            int result = x.Distance.CompareTo(y.Distance);
            return result == 0 ? x.Vertex.CompareTo(y.Vertex) : result;
        }));
        var visited = new HashSet<string>();

        // Initialize distances
        foreach (string vertex in adjacencyList.Keys)
        {
            distances[vertex] = int.MaxValue;
        }
        distances[start] = 0;

        pq.Add(new Node(start, 0));

        while (pq.Count > 0)
        {
            Node current = pq.Min;
            pq.Remove(current);

            string currentVertex = current.Vertex;

            if (visited.Contains(currentVertex))
                continue;

            visited.Add(currentVertex);

            if (adjacencyList.ContainsKey(currentVertex))
            {
                foreach (Edge edge in adjacencyList[currentVertex])
                {
                    if (visited.Contains(edge.Destination))
                        continue;

                    int newDist = distances[currentVertex] + edge.Weight;

                    if (newDist < distances.GetValueOrDefault(edge.Destination, int.MaxValue))
                    {
                        distances[edge.Destination] = newDist;
                        previous[edge.Destination] = currentVertex;
                        pq.Add(new Node(edge.Destination, newDist));
                    }
                }
            }
        }

        return distances;
    }

    public (List<string> path, int distance) GetShortestPath(string start, string end)
    {
        var distances = new Dictionary<string, int>();
        var previous = new Dictionary<string, string>();
        var pq = new SortedSet<Node>(Comparer<Node>.Create((x, y) => 
        {
            int result = x.Distance.CompareTo(y.Distance);
            return result == 0 ? x.Vertex.CompareTo(y.Vertex) : result;
        }));
        var visited = new HashSet<string>();

        // Initialize
        foreach (string vertex in adjacencyList.Keys)
        {
            distances[vertex] = int.MaxValue;
        }
        distances[start] = 0;
        pq.Add(new Node(start, 0));

        while (pq.Count > 0)
        {
            Node current = pq.Min;
            pq.Remove(current);

            string currentVertex = current.Vertex;

            if (visited.Contains(currentVertex))
                continue;

            visited.Add(currentVertex);

            if (currentVertex == end)
                break;

            if (adjacencyList.ContainsKey(currentVertex))
            {
                foreach (Edge edge in adjacencyList[currentVertex])
                {
                    if (visited.Contains(edge.Destination))
                        continue;

                    int newDist = distances[currentVertex] + edge.Weight;

                    if (newDist < distances.GetValueOrDefault(edge.Destination, int.MaxValue))
                    {
                        distances[edge.Destination] = newDist;
                        previous[edge.Destination] = currentVertex;
                        pq.Add(new Node(edge.Destination, newDist));
                    }
                }
            }
        }

        // Reconstruct path
        var path = new List<string>();
        string current = end;

        while (current != null)
        {
            path.Add(current);
            previous.TryGetValue(current, out current);
        }

        path.Reverse();

        if (path.Count > 0 && path[0] == start)
        {
            return (path, distances.GetValueOrDefault(end, int.MaxValue));
        }
        else
        {
            return (new List<string>(), int.MaxValue);
        }
    }

    public int GetShortestDistance(string start, string end)
    {
        var distances = Dijkstra(start);
        return distances.GetValueOrDefault(end, int.MaxValue);
    }

    // Example usage
    static void Main(string[] args)
    {
        WeightedGraph g = new WeightedGraph(false);

        // Add edges
        g.AddEdge("A", "B", 4);
        g.AddEdge("A", "C", 2);
        g.AddEdge("B", "C", 1);
        g.AddEdge("B", "D", 5);
        g.AddEdge("C", "D", 8);
        g.AddEdge("C", "E", 10);
        g.AddEdge("D", "E", 2);

        // Find shortest distances from A
        var distances = g.Dijkstra("A");
        Console.WriteLine($"Shortest distances from A: {string.Join(", ", distances.Select(kvp => $"{kvp.Key}: {kvp.Value}"))}");

        // Find shortest path from A to E
        var (path, distance) = g.GetShortestPath("A", "E");
        Console.WriteLine($"Shortest path from A to E: [{string.Join(", ", path)}] (distance: {distance})");
    }
}

