using System;
using System.Linq;

public class KadanesAlgorithm
{
    public class Result
    {
        public int MaxSum { get; set; }
        public int Start { get; set; }
        public int End { get; set; }

        public Result(int maxSum, int start, int end)
        {
            MaxSum = maxSum;
            Start = start;
            End = end;
        }

        public override string ToString()
        {
            return $"MaxSum: {MaxSum}, Start: {Start}, End: {End}";
        }
    }

    public static Result KadanesAlgorithm(int[] arr)
    {
        if (arr == null || arr.Length == 0)
        {
            return new Result(0, -1, -1);
        }

        int maxEndingHere = arr[0];
        int maxSoFar = arr[0];
        int start = 0, end = 0, tempStart = 0;

        for (int i = 1; i < arr.Length; i++)
        {
            // If current element is greater than sum + current element
            if (arr[i] > maxEndingHere + arr[i])
            {
                maxEndingHere = arr[i];
                tempStart = i;
            }
            else
            {
                maxEndingHere = maxEndingHere + arr[i];
            }

            // Update maximum sum and indices
            if (maxEndingHere > maxSoFar)
            {
                maxSoFar = maxEndingHere;
                start = tempStart;
                end = i;
            }
        }

        return new Result(maxSoFar, start, end);
    }

    public static int KadanesSimple(int[] arr)
    {
        if (arr == null || arr.Length == 0)
        {
            return 0;
        }

        int maxEndingHere = arr[0];
        int maxSoFar = arr[0];

        for (int i = 1; i < arr.Length; i++)
        {
            maxEndingHere = Math.Max(arr[i], maxEndingHere + arr[i]);
            maxSoFar = Math.Max(maxSoFar, maxEndingHere);
        }

        return maxSoFar;
    }

    public static int KadanesWithAllNegative(int[] arr)
    {
        if (arr == null || arr.Length == 0)
        {
            return 0;
        }

        int maxEndingHere = 0;
        int maxSoFar = int.MinValue;

        foreach (int num in arr)
        {
            maxEndingHere = Math.Max(num, maxEndingHere + num);
            maxSoFar = Math.Max(maxSoFar, maxEndingHere);
        }

        return maxSoFar;
    }

    // 2D version for maximum sum rectangle
    public static int MaxSumRectangle(int[,] matrix)
    {
        if (matrix == null || matrix.GetLength(0) == 0)
        {
            return 0;
        }

        int rows = matrix.GetLength(0);
        int cols = matrix.GetLength(1);
        int maxSum = int.MinValue;

        for (int top = 0; top < rows; top++)
        {
            int[] temp = new int[cols];

            for (int bottom = top; bottom < rows; bottom++)
            {
                // Add current row to temp array
                for (int i = 0; i < cols; i++)
                {
                    temp[i] += matrix[bottom, i];
                }

                // Apply Kadane's algorithm on temp array
                int currentMax = KadanesSimple(temp);
                maxSum = Math.Max(maxSum, currentMax);
            }
        }

        return maxSum;
    }

    // Circular array version
    public static int MaxSubarrayCircular(int[] arr)
    {
        if (arr == null || arr.Length == 0)
        {
            return 0;
        }

        // Case 1: Maximum subarray is non-circular
        int maxKadane = KadanesSimple(arr);

        // Case 2: Maximum subarray is circular
        int totalSum = arr.Sum();

        // Invert all elements and find minimum subarray
        int[] invertedArr = arr.Select(x => -x).ToArray();
        int maxInverted = KadanesSimple(invertedArr);
        int minSubarray = -maxInverted;

        int maxCircular = totalSum - minSubarray;

        // Handle case where all elements are negative
        if (maxCircular == 0)
        {
            return maxKadane;
        }

        return Math.Max(maxKadane, maxCircular);
    }

    // Example usage
    static void Main(string[] args)
    {
        int[][] testArrays = {
            new int[] {-2, -3, 4, -1, -2, 1, 5, -3},
            new int[] {1, 2, 3, 4, 5},
            new int[] {-1, -2, -3, -4},
            new int[] {5, -3, 5},
            new int[] {1}
        };

        foreach (int[] arr in testArrays)
        {
            Result result = KadanesAlgorithm(arr);
            int simpleSum = KadanesSimple(arr);

            Console.WriteLine($"Array: [{string.Join(", ", arr)}]");
            Console.WriteLine($"Result: {result}");
            Console.WriteLine($"Simple version: {simpleSum}");
            Console.WriteLine(new string('-', 40));
        }

        // Test 2D version
        int[,] matrix = {
            {1, 2, -1, -4, -20},
            {-8, -3, 4, 2, 1},
            {3, 8, 10, 1, 3},
            {-4, -1, 1, 7, -6}
        };

        Console.WriteLine($"Maximum sum rectangle: {MaxSumRectangle(matrix)}");

        // Test circular version
        int[] circularArray = {1, -2, 3, -2};
        Console.WriteLine($"Maximum circular subarray: {MaxSubarrayCircular(circularArray)}");
    }
}

