// Java Two Pointers Technique Implementation
import java.util.*;

public class TwoPointers {

    // Find two numbers in sorted array that sum to target
    public static int[] twoSumSorted(int[] arr, int target) {
        int left = 0, right = arr.length - 1;

        while (left < right) {
            int currentSum = arr[left] + arr[right];

            if (currentSum == target) {
                return new int[]{left, right};
            } else if (currentSum < target) {
                left++;
            } else {
                right--;
            }
        }

        return new int[]{-1, -1}; // Not found
    }

    // Check if string is palindrome
    public static boolean isPalindrome(String s) {
        // Clean string - keep only alphanumeric
        StringBuilder cleaned = new StringBuilder();
        for (char c : s.toCharArray()) {
            if (Character.isLetterOrDigit(c)) {
                cleaned.append(Character.toLowerCase(c));
            }
        }

        int left = 0, right = cleaned.length() - 1;

        while (left < right) {
            if (cleaned.charAt(left) != cleaned.charAt(right)) {
                return false;
            }
            left++;
            right--;
        }

        return true;
    }

    // Remove duplicates from sorted array in-place
    public static int removeDuplicates(int[] arr) {
        if (arr.length == 0) return 0;

        int writeIndex = 1;

        for (int readIndex = 1; readIndex < arr.length; readIndex++) {
            if (arr[readIndex] != arr[readIndex - 1]) {
                arr[writeIndex] = arr[readIndex];
                writeIndex++;
            }
        }

        return writeIndex;
    }

    // Find all unique triplets that sum to target
    public static List<List<Integer>> threeSum(int[] arr, int target) {
        Arrays.sort(arr);
        List<List<Integer>> result = new ArrayList<>();

        for (int i = 0; i < arr.length - 2; i++) {
            // Skip duplicates for first element
            if (i > 0 && arr[i] == arr[i - 1]) {
                continue;
            }

            int left = i + 1, right = arr.length - 1;

            while (left < right) {
                int currentSum = arr[i] + arr[left] + arr[right];

                if (currentSum == target) {
                    result.add(Arrays.asList(arr[i], arr[left], arr[right]));

                    // Skip duplicates
                    while (left < right && arr[left] == arr[left + 1]) {
                        left++;
                    }
                    while (left < right && arr[right] == arr[right - 1]) {
                        right--;
                    }

                    left++;
                    right--;
                } else if (currentSum < target) {
                    left++;
                } else {
                    right--;
                }
            }
        }

        return result;
    }

    // Find container that can hold most water
    public static int containerWithMostWater(int[] heights) {
        int left = 0, right = heights.length - 1;
        int maxArea = 0;

        while (left < right) {
            // Calculate current area
            int width = right - left;
            int height = Math.min(heights[left], heights[right]);
            int area = width * height;
            maxArea = Math.max(maxArea, area);

            // Move pointer with smaller height
            if (heights[left] < heights[right]) {
                left++;
            } else {
                right--;
            }
        }

        return maxArea;
    }

    // Partition array around pivot value
    public static int partitionArray(int[] arr, int pivot) {
        int left = 0, right = arr.length - 1;

        while (left <= right) {
            // Find element >= pivot from left
            while (left <= right && arr[left] < pivot) {
                left++;
            }

            // Find element < pivot from right
            while (left <= right && arr[right] >= pivot) {
                right--;
            }

            // Swap if both pointers are valid
            if (left < right) {
                int temp = arr[left];
                arr[left] = arr[right];
                arr[right] = temp;
                left++;
                right--;
            }
        }

        return left; // Return partition index
    }

    // Reverse words in string
    public static String reverseWordsInString(String s) {
        char[] chars = s.toCharArray();
        int n = chars.length;

        // Reverse entire string
        reverse(chars, 0, n - 1);

        // Reverse each word
        int start = 0;
        for (int end = 0; end <= n; end++) {
            if (end == n || chars[end] == ' ') {
                reverse(chars, start, end - 1);
                start = end + 1;
            }
        }

        return new String(chars);
    }

    private static void reverse(char[] chars, int left, int right) {
        while (left < right) {
            char temp = chars[left];
            chars[left] = chars[right];
            chars[right] = temp;
            left++;
            right--;
        }
    }

    // Sliding window maximum
    public static int[] slidingWindowMaximum(int[] arr, int k) {
        if (arr.length == 0 || k == 0) return new int[0];

        int[] result = new int[arr.length - k + 1];
        Deque<Integer> deque = new ArrayDeque<>();

        for (int i = 0; i < arr.length; i++) {
            // Remove elements outside window
            while (!deque.isEmpty() && deque.peekFirst() < i - k + 1) {
                deque.pollFirst();
            }

            // Remove smaller elements from rear
            while (!deque.isEmpty() && arr[deque.peekLast()] < arr[i]) {
                deque.pollLast();
            }

            deque.offerLast(i);

            // Add to result when window is complete
            if (i >= k - 1) {
                result[i - k + 1] = arr[deque.peekFirst()];
            }
        }

        return result;
    }

    // Example usage
    public static void main(String[] args) {
        // Test two sum
        int[] twoSumResult = twoSumSorted(new int[]{1, 2, 3, 4, 6}, 6);
        System.out.println("Two Sum: " + Arrays.toString(twoSumResult));

        // Test palindrome
        System.out.println("Palindrome: " + isPalindrome("A man, a plan, a canal: Panama"));

        // Test remove duplicates
        int[] arr = {1, 1, 2, 2, 3, 4, 4, 5};
        int length = removeDuplicates(arr);
        System.out.println("Remove duplicates: " + Arrays.toString(Arrays.copyOf(arr, length)));

        // Test three sum
        List<List<Integer>> threeSumResult = threeSum(new int[]{-1, 0, 1, 2, -1, -4}, 0);
