// Java Heap Sort Implementation
public class HeapSort {

    public static void heapSort(int[] arr) {
        int n = arr.length;

        // Build max heap
        buildMaxHeap(arr);

        // Extract elements from heap one by one
        for (int i = n - 1; i > 0; i--) {
            // Move current root to end
            swap(arr, 0, i);

            // Call heapify on the reduced heap
            heapify(arr, i, 0);
        }
    }

    private static void buildMaxHeap(int[] arr) {
        int n = arr.length;

        // Start from last non-leaf node and heapify each node
        for (int i = n / 2 - 1; i >= 0; i--) {
            heapify(arr, n, i);
        }
    }

    private static void heapify(int[] arr, int n, int i) {
        int largest = i; // Initialize largest as root
        int left = 2 * i + 1;
        int right = 2 * i + 2;

        // Check if left child exists and is greater than root
        if (left < n && arr[left] > arr[largest]) {
            largest = left;
        }

        // Check if right child exists and is greater than largest so far
        if (right < n && arr[right] > arr[largest]) {
            largest = right;
        }

        // If largest is not root, swap and continue heapifying
        if (largest != i) {
            swap(arr, i, largest);
            heapify(arr, n, largest);
        }
    }

    private static void swap(int[] arr, int i, int j) {
        int temp = arr[i];
        arr[i] = arr[j];
        arr[j] = temp;
    }

    // Alternative implementation with iterative heapify
    public static void heapSortIterative(int[] arr) {
        int n = arr.length;

        // Build heap
        for (int i = n / 2 - 1; i >= 0; i--) {
            siftDown(arr, i, n - 1);
        }

        // Extract elements from heap
        for (int end = n - 1; end > 0; end--) {
            swap(arr, 0, end);
            siftDown(arr, 0, end - 1);
        }
    }

    private static void siftDown(int[] arr, int start, int end) {
        int root = start;

        while (root * 2 + 1 <= end) {
            int child = root * 2 + 1;
            int swapIdx = root;

            // Check left child
            if (arr[swapIdx] < arr[child]) {
                swapIdx = child;
            }

            // Check right child
            if (child + 1 <= end && arr[swapIdx] < arr[child + 1]) {
                swapIdx = child + 1;
            }

            if (swapIdx == root) {
                return;
            } else {
                swap(arr, root, swapIdx);
                root = swapIdx;
            }
        }
    }

    // Utility method to print array
    public static void printArray(int[] arr) {
        for (int value : arr) {
            System.out.print(value + " ");
        }
        System.out.println();
    }

    // Example usage
    public static void main(String[] args) {
        int[][] testArrays = {
            {64, 34, 25, 12, 22, 11, 90},
            {5, 2, 8, 1, 9},
            {1},
            {3, 3, 3, 3}
        };

        for (int[] arr : testArrays) {
            int[] original = arr.clone();

            System.out.print("Original: ");
            printArray(original);

            heapSort(arr);
            System.out.print("Sorted:   ");
            printArray(arr);
            System.out.println("-".repeat(40));
        }
    }
}

