// C# Heap Sort Implementation
using System;

public class HeapSort
{
    public static void Sort(int[] arr)
    {
        int n = arr.Length;

        // Build max heap
        BuildMaxHeap(arr);

        // Extract elements from heap one by one
        for (int i = n - 1; i > 0; i--)
        {
            // Move current root to end
            Swap(arr, 0, i);

            // Call heapify on the reduced heap
            Heapify(arr, i, 0);
        }
    }

    private static void BuildMaxHeap(int[] arr)
    {
        int n = arr.Length;

        // Start from last non-leaf node and heapify each node
        for (int i = n / 2 - 1; i >= 0; i--)
        {
            Heapify(arr, n, i);
        }
    }

    private static void Heapify(int[] arr, int n, int i)
    {
        int largest = i; // Initialize largest as root
        int left = 2 * i + 1;
        int right = 2 * i + 2;

        // Check if left child exists and is greater than root
        if (left < n && arr[left] > arr[largest])
        {
            largest = left;
        }

        // Check if right child exists and is greater than largest so far
        if (right < n && arr[right] > arr[largest])
        {
            largest = right;
        }

        // If largest is not root, swap and continue heapifying
        if (largest != i)
        {
            Swap(arr, i, largest);
            Heapify(arr, n, largest);
        }
    }

    private static void Swap(int[] arr, int i, int j)
    {
        (arr[i], arr[j]) = (arr[j], arr[i]);
    }

    // Alternative implementation with iterative heapify
    public static void SortIterative(int[] arr)
    {
        int n = arr.Length;

        // Build heap
        for (int i = n / 2 - 1; i >= 0; i--)
        {
            SiftDown(arr, i, n - 1);
        }

        // Extract elements from heap
        for (int end = n - 1; end > 0; end--)
        {
            Swap(arr, 0, end);
            SiftDown(arr, 0, end - 1);
        }
    }

    private static void SiftDown(int[] arr, int start, int end)
    {
        int root = start;

        while (root * 2 + 1 <= end)
        {
            int child = root * 2 + 1;
            int swapIdx = root;

            // Check left child
            if (arr[swapIdx] < arr[child])
            {
                swapIdx = child;
            }

            // Check right child
            if (child + 1 <= end && arr[swapIdx] < arr[child + 1])
            {
                swapIdx = child + 1;
            }

            if (swapIdx == root)
            {
                return;
            }
            else
            {
                Swap(arr, root, swapIdx);
                root = swapIdx;
            }
        }
    }

    // Generic version for any comparable type
    public static void Sort<T>(T[] arr) where T : IComparable<T>
    {
        int n = arr.Length;

        // Build max heap
        for (int i = n / 2 - 1; i >= 0; i--)
        {
            HeapifyGeneric(arr, n, i);
        }

        // Extract elements from heap one by one
        for (int i = n - 1; i > 0; i--)
        {
            (arr[0], arr[i]) = (arr[i], arr[0]);
            HeapifyGeneric(arr, i, 0);
        }
    }

    private static void HeapifyGeneric<T>(T[] arr, int n, int i) where T : IComparable<T>
    {
        int largest = i;
        int left = 2 * i + 1;
        int right = 2 * i + 2;

        if (left < n && arr[left].CompareTo(arr[largest]) > 0)
        {
            largest = left;
        }

        if (right < n && arr[right].CompareTo(arr[largest]) > 0)
        {
            largest = right;
        }

        if (largest != i)
        {
            (arr[i], arr[largest]) = (arr[largest], arr[i]);
            HeapifyGeneric(arr, n, largest);
        }
    }

    // Example usage
    static void Main(string[] args)
    {
        int[][] testArrays = {
            new int[] {64, 34, 25, 12, 22, 11, 90},
            new int[] {5, 2, 8, 1, 9},
            new int[] {1},
            new int[] {3, 3, 3, 3}
        };

        foreach (int[] arr in testArrays)
        {
            int[] original = (int[])arr.Clone();

            Console.WriteLine($"Original: [{string.Join(", ", original)}]");

            Sort(arr);
            Console.WriteLine($"Sorted:   [{string.Join(", ", arr)}]");
            Console.WriteLine(new string('-', 40));
        }
    }
}

