def longest_common_subsequence(text1, text2):
    """
    Find the longest common subsequence between two strings
    """
    m, n = len(text1), len(text2)

    # Create DP table
    dp = [[0] * (n + 1) for _ in range(m + 1)]

    # Fill the DP table
    for i in range(1, m + 1):
        for j in range(1, n + 1):
            if text1[i - 1] == text2[j - 1]:
                dp[i][j] = dp[i - 1][j - 1] + 1
            else:
                dp[i][j] = max(dp[i - 1][j], dp[i][j - 1])

    return dp[m][n]

def lcs_with_sequence(text1, text2):
    """
    Find LCS length and the actual subsequence
    """
    m, n = len(text1), len(text2)
    dp = [[0] * (n + 1) for _ in range(m + 1)]

    # Fill DP table
    for i in range(1, m + 1):
        for j in range(1, n + 1):
            if text1[i - 1] == text2[j - 1]:
                dp[i][j] = dp[i - 1][j - 1] + 1
            else:
                dp[i][j] = max(dp[i - 1][j], dp[i][j - 1])

    # Reconstruct the LCS
    lcs = []
    i, j = m, n

    while i > 0 and j > 0:
        if text1[i - 1] == text2[j - 1]:
            lcs.append(text1[i - 1])
            i -= 1
            j -= 1
        elif dp[i - 1][j] > dp[i][j - 1]:
            i -= 1
        else:
            j -= 1

    return dp[m][n], ''.join(reversed(lcs))

def lcs_space_optimized(text1, text2):
    """
    Space-optimized version using only two rows
    """
    m, n = len(text1), len(text2)

    # Use only two rows instead of full matrix
    prev = [0] * (n + 1)
    curr = [0] * (n + 1)

    for i in range(1, m + 1):
        for j in range(1, n + 1):
            if text1[i - 1] == text2[j - 1]:
                curr[j] = prev[j - 1] + 1
            else:
                curr[j] = max(prev[j], curr[j - 1])

        # Swap rows
        prev, curr = curr, prev

    return prev[n]

def lcs_multiple_sequences(*sequences):
    """
    Find LCS of multiple sequences (3 or more)
    """
    if len(sequences) < 2:
        return 0 if len(sequences) == 0 else len(sequences[0])

    if len(sequences) == 2:
        return longest_common_subsequence(sequences[0], sequences[1])

    # For multiple sequences, use recursive approach
    def lcs_recursive(seqs, lengths, indices):
        # Check if any sequence is exhausted
        for i, seq in enumerate(seqs):
            if indices[i] >= lengths[i]:
                return 0

        # Check if all current characters are the same
        first_char = seqs[0][indices[0]]
        all_same = all(seqs[i][indices[i]] == first_char for i in range(len(seqs)))

        if all_same:
            # Include this character and move all indices
            new_indices = [idx + 1 for idx in indices]
            return 1 + lcs_recursive(seqs, lengths, new_indices)
        else:
            # Try advancing each sequence one by one and take maximum
            max_length = 0
            for i in range(len(seqs)):
                new_indices = indices[:]
                new_indices[i] += 1
                max_length = max(max_length, lcs_recursive(seqs, lengths, new_indices))
            return max_length

    lengths = [len(seq) for seq in sequences]
    indices = [0] * len(sequences)
    return lcs_recursive(sequences, lengths, indices)

# Example usage and testing
if __name__ == "__main__":
    # Test basic LCS
    text1 = "ABCDGH"
    text2 = "AEDFHR"

    length = longest_common_subsequence(text1, text2)
    print(f"LCS length: {length}")  # Output: 3

    length, sequence = lcs_with_sequence(text1, text2)
    print(f"LCS: '{sequence}' (length: {length})")  # Output: ADH

    # Test space-optimized version
    length_opt = lcs_space_optimized(text1, text2)
    print(f"Space-optimized LCS length: {length_opt}")

    # Test with DNA sequences
    dna1 = "AGGTAB"
    dna2 = "GXTXAYB"
    length, sequence = lcs_with_sequence(dna1, dna2)
    print(f"DNA LCS: '{sequence}' (length: {length})")

    # Test multiple sequences
    seq1, seq2, seq3 = "ABCD", "ACBD", "ABDC"
    multi_lcs = lcs_multiple_sequences(seq1, seq2, seq3)
    print(f"Multiple sequences LCS length: {multi_lcs}")

