// Java LCS Implementation
public class LongestCommonSubsequence {

    public static int longestCommonSubsequence(String text1, String text2) {
        int m = text1.length();
        int n = text2.length();

        // Create DP table
        int[][] dp = new int[m + 1][n + 1];

        // Fill the DP table
        for (int i = 1; i <= m; i++) {
            for (int j = 1; j <= n; j++) {
                if (text1.charAt(i - 1) == text2.charAt(j - 1)) {
                    dp[i][j] = dp[i - 1][j - 1] + 1;
                } else {
                    dp[i][j] = Math.max(dp[i - 1][j], dp[i][j - 1]);
                }
            }
        }

        return dp[m][n];
    }

    public static class LCSResult {
        public int length;
        public String sequence;

        public LCSResult(int length, String sequence) {
            this.length = length;
            this.sequence = sequence;
        }
    }

    public static LCSResult lcsWithSequence(String text1, String text2) {
        int m = text1.length();
        int n = text2.length();
        int[][] dp = new int[m + 1][n + 1];

        // Fill DP table
        for (int i = 1; i <= m; i++) {
            for (int j = 1; j <= n; j++) {
                if (text1.charAt(i - 1) == text2.charAt(j - 1)) {
                    dp[i][j] = dp[i - 1][j - 1] + 1;
                } else {
                    dp[i][j] = Math.max(dp[i - 1][j], dp[i][j - 1]);
                }
            }
        }

        // Reconstruct the LCS
        StringBuilder lcs = new StringBuilder();
        int i = m, j = n;

        while (i > 0 && j > 0) {
            if (text1.charAt(i - 1) == text2.charAt(j - 1)) {
                lcs.append(text1.charAt(i - 1));
                i--;
                j--;
            } else if (dp[i - 1][j] > dp[i][j - 1]) {
                i--;
            } else {
                j--;
            }
        }

        return new LCSResult(dp[m][n], lcs.reverse().toString());
    }

    public static int lcsSpaceOptimized(String text1, String text2) {
        int m = text1.length();
        int n = text2.length();

        // Use only two rows instead of full matrix
        int[] prev = new int[n + 1];
        int[] curr = new int[n + 1];

        for (int i = 1; i <= m; i++) {
            for (int j = 1; j <= n; j++) {
                if (text1.charAt(i - 1) == text2.charAt(j - 1)) {
                    curr[j] = prev[j - 1] + 1;
                } else {
                    curr[j] = Math.max(prev[j], curr[j - 1]);
                }
            }

            // Swap arrays
            int[] temp = prev;
            prev = curr;
            curr = temp;
            java.util.Arrays.fill(curr, 0);
        }

        return prev[n];
    }

    // Memoized recursive approach
    public static int lcsRecursive(String text1, String text2) {
        int[][] memo = new int[text1.length()][text2.length()];
        for (int[] row : memo) {
            java.util.Arrays.fill(row, -1);
        }
        return lcsRecursiveHelper(text1, text2, 0, 0, memo);
    }

    private static int lcsRecursiveHelper(String text1, String text2, int i, int j, int[][] memo) {
        if (i >= text1.length() || j >= text2.length()) {
            return 0;
        }

        if (memo[i][j] != -1) {
            return memo[i][j];
        }

        if (text1.charAt(i) == text2.charAt(j)) {
            memo[i][j] = 1 + lcsRecursiveHelper(text1, text2, i + 1, j + 1, memo);
        } else {
            memo[i][j] = Math.max(
                lcsRecursiveHelper(text1, text2, i + 1, j, memo),
                lcsRecursiveHelper(text1, text2, i, j + 1, memo)
            );
        }

        return memo[i][j];
    }

    // Find all LCS sequences
    public static java.util.Set<String> findAllLCS(String text1, String text2) {
        int m = text1.length();
        int n = text2.length();
        int[][] dp = new int[m + 1][n + 1];

        // Fill DP table
        for (int i = 1; i <= m; i++) {
            for (int j = 1; j <= n; j++) {
                if (text1.charAt(i - 1) == text2.charAt(j - 1)) {
                    dp[i][j] = dp[i - 1][j - 1] + 1;
                } else {
                    dp[i][j] = Math.max(dp[i - 1][j], dp[i][j - 1]);
                }
            }
        }

        java.util.Set<String> result = new java.util.HashSet<>();
        findAllLCSHelper(text1, text2, m, n, dp, "", result);
        return result;
    }

    private static void findAllLCSHelper(String text1, String text2, int i, int j, 
                                        int[][] dp, String current, java.util.Set<String> result) {
        if (i == 0 || j == 0) {
            result.add(new StringBuilder(current).reverse().toString());
            return;
        }

        if (text1.charAt(i - 1) == text2.charAt(j - 1)) {
            findAllLCSHelper(text1, text2, i - 1, j - 1, dp, 
                           current + text1.charAt(i - 1), result);
        } else {
            if (dp[i - 1][j] == dp[i][j]) {
                findAllLCSHelper(text1, text2, i - 1, j, dp, current, result);
            }
            if (dp[i][j - 1] == dp[i][j]) {
                findAllLCSHelper(text1, text2, i, j - 1, dp, current, result);
            }
        }
    }

    // Example usage
    public static void main(String[] args) {
        String text1 = "ABCDGH";
        String text2 = "AEDFHR";

        int length = longestCommonSubsequence(text1, text2);
        System.out.println("LCS length: " + length);

        LCSResult result = lcsWithSequence(text1, text2);
        System.out.println("LCS: '" + result.sequence + "' (length: " + result.length + ")");

        int lengthOpt = lcsSpaceOptimized(text1, text2);
        System.out.println("Space-optimized LCS length: " + lengthOpt);

        // Test with DNA sequences
        String dna1 = "AGGTAB";
        String dna2 = "GXTXAYB";
        LCSResult dnaResult = lcsWithSequence(dna1, dna2);
        System.out.println("DNA LCS: '" + dnaResult.sequence + "' (length: " + dnaResult.length + ")");

        // Find all possible LCS
        java.util.Set<String> allLCS = findAllLCS("ABC", "AC");
        System.out.println("All LCS: " + allLCS);
    }
}

