using System;
using System.Collections.Generic;
using System.Linq;
using System.Text;

public class LongestCommonSubsequence
{
    public static int FindLCS(string text1, string text2)
    {
        int m = text1.Length;
        int n = text2.Length;

        // Create DP table
        int[,] dp = new int[m + 1, n + 1];

        // Fill the DP table
        for (int i = 1; i <= m; i++)
        {
            for (int j = 1; j <= n; j++)
            {
                if (text1[i - 1] == text2[j - 1])
                {
                    dp[i, j] = dp[i - 1, j - 1] + 1;
                }
                else
                {
                    dp[i, j] = Math.Max(dp[i - 1, j], dp[i, j - 1]);
                }
            }
        }

        return dp[m, n];
    }

    public class LCSResult
    {
        public int Length { get; set; }
        public string Sequence { get; set; }

        public LCSResult(int length, string sequence)
        {
            Length = length;
            Sequence = sequence;
        }
    }

    public static LCSResult FindLCSWithSequence(string text1, string text2)
    {
        int m = text1.Length;
        int n = text2.Length;
        int[,] dp = new int[m + 1, n + 1];

        // Fill DP table
        for (int i = 1; i <= m; i++)
        {
            for (int j = 1; j <= n; j++)
            {
                if (text1[i - 1] == text2[j - 1])
                {
                    dp[i, j] = dp[i - 1, j - 1] + 1;
                }
                else
                {
                    dp[i, j] = Math.Max(dp[i - 1, j], dp[i, j - 1]);
                }
            }
        }

        // Reconstruct the LCS
        var lcs = new StringBuilder();
        int x = m, y = n;

        while (x > 0 && y > 0)
        {
            if (text1[x - 1] == text2[y - 1])
            {
                lcs.Append(text1[x - 1]);
                x--;
                y--;
            }
            else if (dp[x - 1, y] > dp[x, y - 1])
            {
                x--;
            }
            else
            {
                y--;
            }
        }

        // Reverse the string since we built it backwards
        var sequence = new string(lcs.ToString().Reverse().ToArray());
        return new LCSResult(dp[m, n], sequence);
    }

    public static int FindLCSSpaceOptimized(string text1, string text2)
    {
        int m = text1.Length;
        int n = text2.Length;

        // Use only two rows instead of full matrix
        int[] prev = new int[n + 1];
        int[] curr = new int[n + 1];

        for (int i = 1; i <= m; i++)
        {
            for (int j = 1; j <= n; j++)
            {
                if (text1[i - 1] == text2[j - 1])
                {
                    curr[j] = prev[j - 1] + 1;
                }
                else
                {
                    curr[j] = Math.Max(prev[j], curr[j - 1]);
                }
            }

            // Swap arrays
            (prev, curr) = (curr, prev);
            Array.Clear(curr, 0, curr.Length);
        }

        return prev[n];
    }

    // Memoized recursive approach
    public static int FindLCSRecursive(string text1, string text2)
    {
        var memo = new Dictionary<(int, int), int>();
        return FindLCSRecursiveHelper(text1, text2, 0, 0, memo);
    }

    private static int FindLCSRecursiveHelper(string text1, string text2, int i, int j, 
                                            Dictionary<(int, int), int> memo)
    {
        if (i >= text1.Length || j >= text2.Length)
        {
            return 0;
        }

        if (memo.ContainsKey((i, j)))
        {
            return memo[(i, j)];
        }

        int result;
        if (text1[i] == text2[j])
        {
            result = 1 + FindLCSRecursiveHelper(text1, text2, i + 1, j + 1, memo);
        }
        else
        {
            result = Math.Max(
                FindLCSRecursiveHelper(text1, text2, i + 1, j, memo),
                FindLCSRecursiveHelper(text1, text2, i, j + 1, memo)
            );
        }

        memo[(i, j)] = result;
        return result;
    }

    // Find all possible LCS sequences
    public static HashSet<string> FindAllLCS(string text1, string text2)
    {
        int m = text1.Length;
        int n = text2.Length;
        int[,] dp = new int[m + 1, n + 1];

        // Fill DP table
        for (int i = 1; i <= m; i++)
        {
            for (int j = 1; j <= n; j++)
            {
                if (text1[i - 1] == text2[j - 1])
                {
                    dp[i, j] = dp[i - 1, j - 1] + 1;
                }
                else
                {
                    dp[i, j] = Math.Max(dp[i - 1, j], dp[i, j - 1]);
                }
            }
        }

        var result = new HashSet<string>();
        FindAllLCSHelper(text1, text2, m, n, dp, "", result);
        return result;
    }

    private static void FindAllLCSHelper(string text1, string text2, int i, int j, 
                                       int[,] dp, string current, HashSet<string> result)
    {
        if (i == 0 || j == 0)
        {
            result.Add(new string(current.Reverse().ToArray()));
            return;
        }

        if (text1[i - 1] == text2[j - 1])
        {
            FindAllLCSHelper(text1, text2, i - 1, j - 1, dp, 
                           current + text1[i - 1], result);
        }
        else
        {
            if (dp[i - 1, j] == dp[i, j])
            {
                FindAllLCSHelper(text1, text2, i - 1, j, dp, current, result);
            }
            if (dp[i, j - 1] == dp[i, j])
            {
                FindAllLCSHelper(text1, text2, i, j - 1, dp, current, result);
            }
        }
    }

    // Similarity percentage based on LCS
    public static double CalculateSimilarity(string text1, string text2)
    {
        if (string.IsNullOrEmpty(text1) && string.IsNullOrEmpty(text2))
            return 100.0;

        if (string.IsNullOrEmpty(text1) || string.IsNullOrEmpty(text2))
            return 0.0;

        int lcsLength = FindLCS(text1, text2);
        int maxLength = Math.Max(text1.Length, text2.Length);

        return (double)lcsLength / maxLength * 100.0;
    }

    // Example usage
    static void Main(string[] args)
    {
        string text1 = "ABCDGH";
        string text2 = "AEDFHR";

        int length = FindLCS(text1, text2);
        Console.WriteLine($"LCS length: {length}");

        var result = FindLCSWithSequence(text1, text2);
        Console.WriteLine($"LCS: '{result.Sequence}' (length: {result.Length})");

        int lengthOpt = FindLCSSpaceOptimized(text1, text2);
        Console.WriteLine($"Space-optimized LCS length: {lengthOpt}");

        // Test with DNA sequences
        string dna1 = "AGGTAB";
        string dna2 = "GXTXAYB";
        var dnaResult = FindLCSWithSequence(dna1, dna2);
        Console.WriteLine($"DNA LCS: '{dnaResult.Sequence}' (length: {dnaResult.Length})");

        // Calculate similarity
        double similarity = CalculateSimilarity(text1, text2);
        Console.WriteLine($"Similarity: {similarity:F1}%");

        // Find all possible LCS
        var allLCS = FindAllLCS("ABC", "AC");
        Console.WriteLine($"All LCS: [{string.Join(", ", allLCS.Select(s => $"'{s}'"))}]");
    }
}

