// C# Knapsack Implementation
using System;
using System.Collections.Generic;
using System.Linq;

public class KnapsackProblem
{
    public static int Knapsack01(int[] weights, int[] values, int capacity)
    {
        int n = weights.Length;

        // Create DP table
        int[,] dp = new int[n + 1, capacity + 1];

        // Fill the DP table
        for (int i = 1; i <= n; i++)
        {
            for (int w = 1; w <= capacity; w++)
            {
                if (weights[i - 1] > w)
                {
                    dp[i, w] = dp[i - 1, w];
                }
                else
                {
                    int include = values[i - 1] + dp[i - 1, w - weights[i - 1]];
                    int exclude = dp[i - 1, w];
                    dp[i, w] = Math.Max(include, exclude);
                }
            }
        }

        return dp[n, capacity];
    }

    public class KnapsackResult
    {
        public int MaxValue { get; set; }
        public List<int> SelectedItems { get; set; }

        public KnapsackResult(int maxValue, List<int> selectedItems)
        {
            MaxValue = maxValue;
            SelectedItems = selectedItems;
        }
    }

    public static KnapsackResult KnapsackWithItems(int[] weights, int[] values, int capacity)
    {
        int n = weights.Length;
        int[,] dp = new int[n + 1, capacity + 1];

        // Fill DP table
        for (int i = 1; i <= n; i++)
        {
            for (int w = 1; w <= capacity; w++)
            {
                if (weights[i - 1] > w)
                {
                    dp[i, w] = dp[i - 1, w];
                }
                else
                {
                    int include = values[i - 1] + dp[i - 1, w - weights[i - 1]];
                    int exclude = dp[i - 1, w];
                    dp[i, w] = Math.Max(include, exclude);
                }
            }
        }

        // Backtrack to find selected items
        var selectedItems = new List<int>();
        int weight = capacity;

        for (int i = n; i > 0; i--)
        {
            if (dp[i, weight] != dp[i - 1, weight])
            {
                selectedItems.Add(i - 1); // Store 0-based index
                weight -= weights[i - 1];
            }
        }

        selectedItems.Reverse();
        return new KnapsackResult(dp[n, capacity], selectedItems);
    }

    public static int KnapsackSpaceOptimized(int[] weights, int[] values, int capacity)
    {
        int n = weights.Length;
        int[] dp = new int[capacity + 1];

        for (int i = 0; i < n; i++)
        {
            // Traverse from right to left
            for (int w = capacity; w >= weights[i]; w--)
            {
                dp[w] = Math.Max(dp[w], dp[w - weights[i]] + values[i]);
            }
        }

        return dp[capacity];
    }

    public static int UnboundedKnapsack(int[] weights, int[] values, int capacity)
    {
        int n = weights.Length;
        int[] dp = new int[capacity + 1];

        for (int w = 1; w <= capacity; w++)
        {
            for (int i = 0; i < n; i++)
            {
                if (weights[i] <= w)
                {
                    dp[w] = Math.Max(dp[w], dp[w - weights[i]] + values[i]);
                }
            }
        }

        return dp[capacity];
    }

    public class FractionalResult
    {
        public double MaxValue { get; set; }
        public List<(int itemIndex, double fraction)> SelectedItems { get; set; }

        public FractionalResult(double maxValue, List<(int, double)> selectedItems)
        {
            MaxValue = maxValue;
            SelectedItems = selectedItems;
        }
    }

    public static FractionalResult FractionalKnapsack(int[] weights, int[] values, int capacity)
    {
        int n = weights.Length;

        // Create items with value-to-weight ratio
        var items = Enumerable.Range(0, n)
            .Select(i => new { Ratio = (double)values[i] / weights[i], Weight = weights[i], Value = values[i], Index = i })
            .OrderByDescending(item => item.Ratio)
            .ToList();

        double totalValue = 0;
        var selectedItems = new List<(int, double)>();
        int remainingCapacity = capacity;

        foreach (var item in items)
        {
            if (remainingCapacity >= item.Weight)
            {
                // Take the whole item
                totalValue += item.Value;
                remainingCapacity -= item.Weight;
                selectedItems.Add((item.Index, 1.0));
            }
            else if (remainingCapacity > 0)
            {
                // Take fraction of the item
                double fraction = (double)remainingCapacity / item.Weight;
                totalValue += item.Value * fraction;
                selectedItems.Add((item.Index, fraction));
                remainingCapacity = 0;
                break;
            }
        }

        return new FractionalResult(totalValue, selectedItems);
    }

    // Memoized recursive approach
    public static int KnapsackRecursive(int[] weights, int[] values, int capacity)
    {
        var memo = new Dictionary<(int, int), int>();
        return KnapsackRecursiveHelper(weights, values, capacity, 0, memo);
    }

    private static int KnapsackRecursiveHelper(int[] weights, int[] values, int capacity, 
                                              int index, Dictionary<(int, int), int> memo)
    {
        if (index >= weights.Length || capacity <= 0)
        {
            return 0;
        }

        var key = (index, capacity);
        if (memo.ContainsKey(key))
        {
            return memo[key];
        }

        int result;
        if (weights[index] > capacity)
        {
            // Cannot include current item
            result = KnapsackRecursiveHelper(weights, values, capacity, index + 1, memo);
        }
        else
        {
            // Choose maximum of including or excluding current item
            int include = values[index] + 
                         KnapsackRecursiveHelper(weights, values, capacity - weights[index], index + 1, memo);
            int exclude = KnapsackRecursiveHelper(weights, values, capacity, index + 1, memo);
            result = Math.Max(include, exclude);
        }

        memo[key] = result;
        return result;
    }

    // Multiple constraints knapsack
    public static int KnapsackMultipleConstraints(int[] weights, int[] values, int capacity, 
                                                 int[] volumes, int volumeLimit)
    {
        int n = weights.Length;

        // 3D DP table
        int[,,] dp = new int[n + 1, capacity + 1, volumeLimit + 1];

        for (int i = 1; i <= n; i++)
        {
            for (int w = 0; w <= capacity; w++)
            {
                for (int v = 0; v <= volumeLimit; v++)
                {
                    // Don't include current item
                    dp[i, w, v] = dp[i - 1, w, v];

                    // Include current item if constraints allow
                    if (weights[i - 1] <= w && volumes[i - 1] <= v)
                    {
                        int includeValue = values[i - 1] + 
                                         dp[i - 1, w - weights[i - 1], v - volumes[i - 1]];
                        dp[i, w, v] = Math.Max(dp[i, w, v], includeValue);
                    }
                }
            }
        }

        return dp[n, capacity, volumeLimit];
    }

    // Calculate efficiency ratio
    public static double CalculateEfficiency(int[] weights, int[] values, List<int> selectedItems)
    {
        int totalWeight = selectedItems.Sum(i => weights[i]);
        int totalValue = selectedItems.Sum(i => values[i]);

        return totalWeight > 0 ? (double)totalValue / totalWeight : 0;
    }

    // Example usage
    static void Main(string[] args)
    {
        int[] weights = {10, 20, 30};
        int[] values = {60, 100, 120};
        int capacity = 50;

        int maxValue = Knapsack01(weights, values, capacity);
        Console.WriteLine($"0/1 Knapsack max value: {maxValue}");

        var result = KnapsackWithItems(weights, values, capacity);
        Console.WriteLine($"Selected items: [{string.Join(", ", result.SelectedItems)}]");
        Console.WriteLine($"Max value with items: {result.MaxValue}");

        int maxValueOpt = KnapsackSpaceOptimized(weights, values, capacity);
        Console.WriteLine($"Space-optimized max value: {maxValueOpt}");

        int unboundedValue = UnboundedKnapsack(weights, values, capacity);
        Console.WriteLine($"Unbounded knapsack max value: {unboundedValue}");

        var fracResult = FractionalKnapsack(weights, values, capacity);
        Console.WriteLine($"Fractional knapsack max value: {fracResult.MaxValue:F2}");

        int recursiveValue = KnapsackRecursive(weights, values, capacity);
        Console.WriteLine($"Recursive knapsack max value: {recursiveValue}");

        // Calculate efficiency
        double efficiency = CalculateEfficiency(weights, values, result.SelectedItems);
        Console.WriteLine($"Selection efficiency: {efficiency:F2}");

        // Test multiple constraints
        int[] volumes = {5, 10, 15};
        int volumeLimit = 25;
        int multiValue = KnapsackMultipleConstraints(weights, values, capacity, volumes, volumeLimit);
        Console.WriteLine($"Multi-constraint knapsack max value: {multiValue}");
    }
}

