// Java Sliding Window Implementation
import java.util.*;

public class SlidingWindow {

    // Maximum sum of subarray of size k
    public static int maxSumSubarrayK(int[] arr, int k) {
        if (arr.length < k) {
            return -1;
        }

        // Calculate sum of first window
        int windowSum = 0;
        for (int i = 0; i < k; i++) {
            windowSum += arr[i];
        }

        int maxSum = windowSum;

        // Slide the window
        for (int i = k; i < arr.length; i++) {
            windowSum = windowSum - arr[i - k] + arr[i];
            maxSum = Math.max(maxSum, windowSum);
        }

        return maxSum;
    }

    // Longest substring without repeating characters (LeetCode 3)
    public static int lengthOfLongestSubstring(String s) {
        Set<Character> charSet = new HashSet<>();
        int left = 0;
        int maxLength = 0;

        for (int right = 0; right < s.length(); right++) {
            // Shrink window until no duplicates
            while (charSet.contains(s.charAt(right))) {
                charSet.remove(s.charAt(left));
                left++;
            }

            charSet.add(s.charAt(right));
            maxLength = Math.max(maxLength, right - left + 1);
        }

        return maxLength;
    }

    // Minimum window substring (LeetCode 76)
    public static String minWindow(String s, String t) {
        if (s.length() == 0 || t.length() == 0) {
            return "";
        }

        // Count characters in t
        Map<Character, Integer> dictT = new HashMap<>();
        for (char c : t.toCharArray()) {
            dictT.put(c, dictT.getOrDefault(c, 0) + 1);
        }

        int required = dictT.size();
        int left = 0, right = 0;
        int formed = 0;

        Map<Character, Integer> windowCounts = new HashMap<>();
        int[] ans = {Integer.MAX_VALUE, 0, 0}; // length, left, right

        while (right < s.length()) {
            // Add character from right to window
            char character = s.charAt(right);
            windowCounts.put(character, windowCounts.getOrDefault(character, 0) + 1);

            // Check if current character's frequency matches desired frequency
            if (dictT.containsKey(character) && 
                windowCounts.get(character).intValue() == dictT.get(character).intValue()) {
                formed++;
            }

            // Try to contract window
            while (left <= right && formed == required) {
                character = s.charAt(left);

                // Save the smallest window
                if (right - left + 1 < ans[0]) {
                    ans[0] = right - left + 1;
                    ans[1] = left;
                    ans[2] = right;
                }

                // Remove character from left
                windowCounts.put(character, windowCounts.get(character) - 1);
                if (dictT.containsKey(character) && 
                    windowCounts.get(character) < dictT.get(character)) {
                    formed--;
                }

                left++;
            }

            right++;
        }

        return ans[0] == Integer.MAX_VALUE ? "" : s.substring(ans[1], ans[2] + 1);
    }

    // Maximum consecutive ones with k flips (LeetCode 1004)
    public static int longestOnes(int[] nums, int k) {
        int left = 0;
        int zeroCount = 0;
        int maxLength = 0;

        for (int right = 0; right < nums.length; right++) {
            if (nums[right] == 0) {
                zeroCount++;
            }

            // Shrink window if we have more than k zeros
            while (zeroCount > k) {
                if (nums[left] == 0) {
                    zeroCount--;
                }
                left++;
            }

            maxLength = Math.max(maxLength, right - left + 1);
        }

        return maxLength;
    }

    // Find all anagrams in string (LeetCode 438)
    public static List<Integer> findAnagrams(String s, String p) {
        List<Integer> result = new ArrayList<>();
        if (p.length() > s.length()) {
            return result;
        }

        // Count characters in p
        Map<Character, Integer> pCount = new HashMap<>();
        for (char c : p.toCharArray()) {
            pCount.put(c, pCount.getOrDefault(c, 0) + 1);
        }

        Map<Character, Integer> windowCount = new HashMap<>();
        int windowSize = p.length();

        for (int i = 0; i < s.length(); i++) {
            // Add character to window
            char c = s.charAt(i);
            windowCount.put(c, windowCount.getOrDefault(c, 0) + 1);

            // Remove character from left if window is too large
            if (i >= windowSize) {
                char leftChar = s.charAt(i - windowSize);
                windowCount.put(leftChar, windowCount.get(leftChar) - 1);
                if (windowCount.get(leftChar) == 0) {
                    windowCount.remove(leftChar);
                }
            }

            // Check if current window is an anagram
            if (windowCount.equals(pCount)) {
                result.add(i - windowSize + 1);
            }
        }

        return result;
    }

    // Longest repeating character replacement (LeetCode 424)
    public static int characterReplacement(String s, int k) {
        Map<Character, Integer> charCount = new HashMap<>();
        int left = 0;
        int maxCount = 0;
        int maxLength = 0;

        for (int right = 0; right < s.length(); right++) {
            char c = s.charAt(right);
            charCount.put(c, charCount.getOrDefault(c, 0) + 1);
            maxCount = Math.max(maxCount, charCount.get(c));

            // If window size - max_count > k, shrink window
            if (right - left + 1 - maxCount > k) {
                char leftChar = s.charAt(left);
                charCount.put(leftChar, charCount.get(leftChar) - 1);
                left++;
            }

            maxLength = Math.max(maxLength, right - left + 1);
        }

        return maxLength;
    }

    // Example usage
    public static void main(String[] args) {
        // Test maximum sum subarray
        int[] arr = {2, 1, 5, 1, 3, 2};
        int k = 3;
        System.out.println("Max sum of subarray size " + k + ": " + maxSumSubarrayK(arr, k));

        // Test longest substring without repeating
        String s = "abcabcbb";
        System.out.println("Longest substring without repeating: " + lengthOfLongestSubstring(s));

        // Test minimum window substring
        String s1 = "ADOBECODEBANC", t = "ABC";
        System.out.println("Minimum window substring: '" + minWindow(s1, t) + "'");

        // Test find all anagrams
        String s2 = "abab", p = "ab";
        System.out.println("Anagram indices: " + findAnagrams(s2, p));
    }
}

