// C# Sliding Window Implementation
using System;
using System.Collections.Generic;
using System.Linq;

public class SlidingWindow
{
    // Maximum sum of subarray of size k
    public static int MaxSumSubarrayK(int[] arr, int k)
    {
        if (arr.Length < k)
        {
            return -1;
        }

        // Calculate sum of first window
        int windowSum = arr.Take(k).Sum();
        int maxSum = windowSum;

        // Slide the window
        for (int i = k; i < arr.Length; i++)
        {
            windowSum = windowSum - arr[i - k] + arr[i];
            maxSum = Math.Max(maxSum, windowSum);
        }

        return maxSum;
    }

    // Longest substring without repeating characters (LeetCode 3)
    public static int LengthOfLongestSubstring(string s)
    {
        var charSet = new HashSet<char>();
        int left = 0;
        int maxLength = 0;

        for (int right = 0; right < s.Length; right++)
        {
            // Shrink window until no duplicates
            while (charSet.Contains(s[right]))
            {
                charSet.Remove(s[left]);
                left++;
            }

            charSet.Add(s[right]);
            maxLength = Math.Max(maxLength, right - left + 1);
        }

        return maxLength;
    }

    // Minimum window substring (LeetCode 76)
    public static string MinWindow(string s, string t)
    {
        if (s.Length == 0 || t.Length == 0)
        {
            return "";
        }

        // Count characters in t
        var dictT = new Dictionary<char, int>();
        foreach (char c in t)
        {
            dictT[c] = dictT.GetValueOrDefault(c, 0) + 1;
        }

        int required = dictT.Count;
        int left = 0, right = 0;
        int formed = 0;

        var windowCounts = new Dictionary<char, int>();
        var ans = new int[] { int.MaxValue, 0, 0 }; // length, left, right

        while (right < s.Length)
        {
            // Add character from right to window
            char character = s[right];
            windowCounts[character] = windowCounts.GetValueOrDefault(character, 0) + 1;

            // Check if current character's frequency matches desired frequency
            if (dictT.ContainsKey(character) && 
                windowCounts[character] == dictT[character])
            {
                formed++;
            }

            // Try to contract window
            while (left <= right && formed == required)
            {
                character = s[left];

                // Save the smallest window
                if (right - left + 1 < ans[0])
                {
                    ans[0] = right - left + 1;
                    ans[1] = left;
                    ans[2] = right;
                }

                // Remove character from left
                windowCounts[character]--;
                if (dictT.ContainsKey(character) && 
                    windowCounts[character] < dictT[character])
                {
                    formed--;
                }

                left++;
            }

            right++;
        }

        return ans[0] == int.MaxValue ? "" : s.Substring(ans[1], ans[0]);
    }

    // Maximum consecutive ones with k flips (LeetCode 1004)
    public static int LongestOnes(int[] nums, int k)
    {
        int left = 0;
        int zeroCount = 0;
        int maxLength = 0;

        for (int right = 0; right < nums.Length; right++)
        {
            if (nums[right] == 0)
            {
                zeroCount++;
            }

            // Shrink window if we have more than k zeros
            while (zeroCount > k)
            {
                if (nums[left] == 0)
                {
                    zeroCount--;
                }
                left++;
            }

            maxLength = Math.Max(maxLength, right - left + 1);
        }

        return maxLength;
    }

    // Find all anagrams in string (LeetCode 438)
    public static List<int> FindAnagrams(string s, string p)
    {
        var result = new List<int>();
        if (p.Length > s.Length)
        {
            return result;
        }

        // Count characters in p
        var pCount = new Dictionary<char, int>();
        foreach (char c in p)
        {
            pCount[c] = pCount.GetValueOrDefault(c, 0) + 1;
        }

        var windowCount = new Dictionary<char, int>();
        int windowSize = p.Length;

        for (int i = 0; i < s.Length; i++)
        {
            // Add character to window
            char c = s[i];
            windowCount[c] = windowCount.GetValueOrDefault(c, 0) + 1;

            // Remove character from left if window is too large
            if (i >= windowSize)
            {
                char leftChar = s[i - windowSize];
                windowCount[leftChar]--;
                if (windowCount[leftChar] == 0)
                {
                    windowCount.Remove(leftChar);
                }
            }

            // Check if current window is an anagram
            if (windowCount.Count == pCount.Count && 
                windowCount.All(kvp => pCount.ContainsKey(kvp.Key) && pCount[kvp.Key] == kvp.Value))
            {
                result.Add(i - windowSize + 1);
            }
        }

        return result;
    }

    // Longest repeating character replacement (LeetCode 424)
    public static int CharacterReplacement(string s, int k)
    {
        var charCount = new Dictionary<char, int>();
        int left = 0;
        int maxCount = 0;
        int maxLength = 0;

        for (int right = 0; right < s.Length; right++)
        {
            char c = s[right];
            charCount[c] = charCount.GetValueOrDefault(c, 0) + 1;
            maxCount = Math.Max(maxCount, charCount[c]);

            // If window size - max_count > k, shrink window
            if (right - left + 1 - maxCount > k)
            {
                char leftChar = s[left];
                charCount[leftChar]--;
                left++;
            }

            maxLength = Math.Max(maxLength, right - left + 1);
        }

        return maxLength;
    }

    // Example usage
    static void Main(string[] args)
    {
        // Test maximum sum subarray
        int[] arr = {2, 1, 5, 1, 3, 2};
        int k = 3;
        Console.WriteLine($"Max sum of subarray size {k}: {MaxSumSubarrayK(arr, k)}");

        // Test longest substring without repeating
        string s = "abcabcbb";
        Console.WriteLine($"Longest substring without repeating: {LengthOfLongestSubstring(s)}");

        // Test minimum window substring
        string s1 = "ADOBECODEBANC", t = "ABC";
        Console.WriteLine($"Minimum window substring: '{MinWindow(s1, t)}'");

        // Test find all anagrams
        string s2 = "abab", p = "ab";
        Console.WriteLine($"Anagram indices: [{string.Join(", ", FindAnagrams(s2, p))}]");
    }
}

