public class Backtracking
{
    // Generate all permutations (LeetCode 46)
    public static List<List<int>> Permute(int[] nums)
    {
        var result = new List<List<int>>();
        BacktrackPermutations(nums, new List<int>(), result);
        return result;
    }

    private static void BacktrackPermutations(int[] nums, List<int> current, 
                                            List<List<int>> result)
    {
        // Base case: permutation is complete
        if (current.Count == nums.Length)
        {
            result.Add(new List<int>(current));
            return;
        }

        // Try each number not yet used
        foreach (int num in nums)
        {
            if (!current.Contains(num))
            {
                current.Add(num);
                BacktrackPermutations(nums, current, result);
                current.RemoveAt(current.Count - 1); // Backtrack
            }
        }
    }

    // Generate combinations (LeetCode 77)
    public static List<List<int>> Combine(int n, int k)
    {
        var result = new List<List<int>>();
        BacktrackCombinations(1, n, k, new List<int>(), result);
        return result;
    }

    private static void BacktrackCombinations(int start, int n, int k, 
                                            List<int> current, List<List<int>> result)
    {
        // Base case: combination is complete
        if (current.Count == k)
        {
            result.Add(new List<int>(current));
            return;
        }

        // Try numbers from start to n
        for (int i = start; i <= n; i++)
        {
            current.Add(i);
            BacktrackCombinations(i + 1, n, k, current, result);
            current.RemoveAt(current.Count - 1); // Backtrack
        }
    }

    // Solve N-Queens (LeetCode 51)
    public static List<List<string>> SolveNQueens(int n)
    {
        var result = new List<List<string>>();
        var board = new char[n][];

        // Initialize board
        for (int i = 0; i < n; i++)
        {
            board[i] = new char[n];
            Array.Fill(board[i], '.');
        }

        BacktrackNQueens(board, 0, result);
        return result;
    }

    private static void BacktrackNQueens(char[][] board, int row, List<List<string>> result)
    {
        int n = board.Length;

        // Base case: all queens placed
        if (row == n)
        {
            var solution = new List<string>();
            foreach (var r in board)
            {
                solution.Add(new string(r));
            }
            result.Add(solution);
            return;
        }

        // Try placing queen in each column of current row
        for (int col = 0; col < n; col++)
        {
            if (IsSafe(board, row, col))
            {
                board[row][col] = 'Q';
                BacktrackNQueens(board, row + 1, result);
                board[row][col] = '.'; // Backtrack
            }
        }
    }

    private static bool IsSafe(char[][] board, int row, int col)
    {
        int n = board.Length;

        // Check column
        for (int i = 0; i < row; i++)
        {
            if (board[i][col] == 'Q')
            {
                return false;
            }
        }

        // Check diagonal (top-left to bottom-right)
        for (int i = row - 1, j = col - 1; i >= 0 && j >= 0; i--, j--)
        {
            if (board[i][j] == 'Q')
            {
                return false;
            }
        }

        // Check diagonal (top-right to bottom-left)
        for (int i = row - 1, j = col + 1; i >= 0 && j < n; i--, j++)
        {
            if (board[i][j] == 'Q')
            {
                return false;
            }
        }

        return true;
    }

    // Generate valid parentheses (LeetCode 22)
    public static List<string> GenerateParenthesis(int n)
    {
        var result = new List<string>();
        BacktrackParentheses("", 0, 0, n, result);
        return result;
    }

    private static void BacktrackParentheses(string current, int open, int close, 
                                           int n, List<string> result)
    {
        // Base case: string is complete
        if (current.Length == 2 * n)
        {
            result.Add(current);
            return;
        }

        // Add opening parenthesis if we haven't used all
        if (open < n)
        {
            BacktrackParentheses(current + "(", open + 1, close, n, result);
        }

        // Add closing parenthesis if it won't make string invalid
        if (close < open)
        {
            BacktrackParentheses(current + ")", open, close + 1, n, result);
        }
    }

    // Word search in 2D board (LeetCode 79)
    public static bool Exist(char[][] board, string word)
    {
        if (board == null || board.Length == 0 || board[0].Length == 0)
        {
            return false;
        }

        int rows = board.Length;
        int cols = board[0].Length;

        // Try starting from each cell
        for (int i = 0; i < rows; i++)
        {
            for (int j = 0; j < cols; j++)
            {
                if (BacktrackWordSearch(board, word, i, j, 0))
                {
                    return true;
                }
            }
        }

        return false;
    }

    private static bool BacktrackWordSearch(char[][] board, string word, 
                                          int row, int col, int index)
    {
        // Base case: found the word
        if (index == word.Length)
        {
            return true;
        }

        // Check bounds and character match
        if (row < 0 || row >= board.Length || col < 0 || col >= board[0].Length ||
            board[row][col] != word[index])
        {
            return false;
        }

        // Mark cell as visited
        char temp = board[row][col];
        board[row][col] = '#';

        // Explore all 4 directions
        bool found = BacktrackWordSearch(board, word, row + 1, col, index + 1) ||
                    BacktrackWordSearch(board, word, row - 1, col, index + 1) ||
                    BacktrackWordSearch(board, word, row, col + 1, index + 1) ||
                    BacktrackWordSearch(board, word, row, col - 1, index + 1);

        // Restore cell value (backtrack)
        board[row][col] = temp;

        return found;
    }

    // Subset sum problem
    public static bool CanPartition(int[] nums, int target)
    {
        return BacktrackSubsetSum(nums, 0, target);
    }

    private static bool BacktrackSubsetSum(int[] nums, int index, int target)
    {
        // Base case: found target sum
        if (target == 0)
        {
            return true;
        }

        // Base case: exceeded target or no more numbers
        if (target < 0 || index >= nums.Length)
        {
            return false;
        }

        // Include current number or exclude it
        return BacktrackSubsetSum(nums, index + 1, target - nums[index]) ||
               BacktrackSubsetSum(nums, index + 1, target);
    }

    // Example usage
    static void Main(string[] args)
    {
        // Test permutations
        int[] nums = {1, 2, 3};
        var permutations = Permute(nums);
        Console.WriteLine($"Permutations: [{string.Join(", ", permutations.Select(p => $"[{string.Join(", ", p)}]"))}]");

        // Test combinations
        var combinations = Combine(4, 2);
        Console.WriteLine($"Combinations C(4,2): [{string.Join(", ", combinations.Select(c => $"[{string.Join(", ", c)}]"))}]");

        // Test N-Queens
        var nQueensSolutions = SolveNQueens(4);
        Console.WriteLine($"4-Queens solutions: {nQueensSolutions.Count} found");

        // Test parentheses generation
        var parentheses = GenerateParenthesis(3);
        Console.WriteLine($"Valid parentheses (n=3): [{string.Join(", ", parentheses.Select(p => $"'{p}'"))}]");

        // Test subset sum
        int[] subsetNums = {3, 34, 4, 12, 5, 2};
        int target = 9;
        Console.WriteLine($"Subset sum exists for target {target}: {CanPartition(subsetNums, target)}");
    }
